INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16396, 'nvi', 'Salmos', 'sl', 150, 1, 'Aleluia! Louvem a Deus no seu santuário, louvem-no no seu poderoso firmamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16397, 'nvi', 'Salmos', 'sl', 150, 2, 'Louvem-no pelos seus feitos poderosos, louvem-no segundo a imensidão de sua grandeza!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16398, 'nvi', 'Salmos', 'sl', 150, 3, 'Louvem-no ao som de trombeta, louvem-no com a lira e a harpa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16399, 'nvi', 'Salmos', 'sl', 150, 4, 'louvem-no com tamborins e danças, louvem-no com instrumentos de cordas e com flautas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16400, 'nvi', 'Salmos', 'sl', 150, 5, 'louvem-no com címbalos sonoros, louvem-no com címbalos ressonantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16401, 'nvi', 'Salmos', 'sl', 150, 6, 'Tudo o que tem vida louve o Senhor! Aleluia!');
