INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14094, 'nvi', 'Salmos', 'sl', 16, 1, 'Protege-me, ó Deus, pois em ti me refugio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14095, 'nvi', 'Salmos', 'sl', 16, 2, 'Ao Senhor declaro: "Tu és o meu Senhor; não tenho bem nenhum além de ti".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14096, 'nvi', 'Salmos', 'sl', 16, 3, 'Quanto aos fiéis que há na terra, eles é que são os notáveis em quem está todo o meu prazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14097, 'nvi', 'Salmos', 'sl', 16, 4, 'Grande será o sofrimento dos que correm atrás de outros deuses. Não participarei dos seus sacrifícios de sangue, e os meus lábios nem mencionarão os seus nomes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14098, 'nvi', 'Salmos', 'sl', 16, 5, 'Senhor, tu és a minha porção e o meu cálice; és tu que garantes o meu futuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14099, 'nvi', 'Salmos', 'sl', 16, 6, 'As divisas caíram para mim em lugares agradáveis: Tenho uma bela herança!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14100, 'nvi', 'Salmos', 'sl', 16, 7, 'Bendirei o Senhor, que me aconselha; na escura noite o meu coração me ensina!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14101, 'nvi', 'Salmos', 'sl', 16, 8, 'Sempre tenho o Senhor diante de mim. Com ele à minha direita, não serei abalado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14102, 'nvi', 'Salmos', 'sl', 16, 9, 'Por isso o meu coração se alegra e no íntimo exulto; mesmo o meu corpo repousará tranqüilo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14103, 'nvi', 'Salmos', 'sl', 16, 10, 'porque tu não me abandonarás no sepulcro, nem permitirás que o teu santo sofra decomposição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14104, 'nvi', 'Salmos', 'sl', 16, 11, 'Tu me farás conhecer a vereda da vida, a alegria plena da tua presença, eterno prazer à tua direita.');
