INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13947, 'nvi', 'Salmos', 'sl', 2, 1, 'Por que se amotinam as nações e os povos tramam em vão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13948, 'nvi', 'Salmos', 'sl', 2, 2, 'Os reis da terra tomam posição e os governantes conspiram unidos contra o Senhor e contra o seu ungido, e dizem:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13949, 'nvi', 'Salmos', 'sl', 2, 3, '"Façamos em pedaços as suas correntes, lancemos de nós as suas algemas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13950, 'nvi', 'Salmos', 'sl', 2, 4, 'Do seu trono nos céus o Senhor põe-se a rir e caçoa deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13951, 'nvi', 'Salmos', 'sl', 2, 5, 'Em sua ira os repreende e em seu furor os aterroriza, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13952, 'nvi', 'Salmos', 'sl', 2, 6, '"Eu mesmo estabeleci o meu rei em Sião, no meu santo monte".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13953, 'nvi', 'Salmos', 'sl', 2, 7, 'Proclamarei o decreto do Senhor: Ele me disse: "Tu és meu filho; eu hoje te gerei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13954, 'nvi', 'Salmos', 'sl', 2, 8, 'Pede-me, e te darei as nações como herança e os confins da terra como tua propriedade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13955, 'nvi', 'Salmos', 'sl', 2, 9, 'Tu as quebrarás com vara de ferro e as despedaçarás como a um vaso de barro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13956, 'nvi', 'Salmos', 'sl', 2, 10, 'Por isso, ó reis, sejam prudentes; aceitem a advertência, autoridades da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13957, 'nvi', 'Salmos', 'sl', 2, 11, 'Adorem ao Senhor com temor; exultem com tremor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13958, 'nvi', 'Salmos', 'sl', 2, 12, 'Beijem o filho, para que ele não se ire e vocês não sejam destruídos de repente, pois num instante acende-se a sua ira. Como são felizes todos os que nele se refugiam!');
