INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14184, 'nvi', 'Salmos', 'sl', 20, 1, 'Que o Senhor te responda no tempo da angústia; o nome do Deus de Jacó te proteja!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14185, 'nvi', 'Salmos', 'sl', 20, 2, 'Do santuário te envie auxílio e de Sião te dê apoio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14186, 'nvi', 'Salmos', 'sl', 20, 3, 'Lembre-se de todas as tuas ofertas e aceite os teus holocaustos. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14187, 'nvi', 'Salmos', 'sl', 20, 4, 'Conceda-te o desejo do teu coração e leve a efeito todos os teus planos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14188, 'nvi', 'Salmos', 'sl', 20, 5, 'Saudaremos a tua vitória com gritos de alegria e ergueremos as nossas bandeiras em nome do nosso Deus. Que o Senhor atenda todos os teus pedidos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14189, 'nvi', 'Salmos', 'sl', 20, 6, 'Agora sei que o Senhor dará vitória ao seu ungido; dos seus santos céus lhe responde com o poder salvador da sua mão direita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14190, 'nvi', 'Salmos', 'sl', 20, 7, 'Alguns confiam em carros e outros em cavalos, mas nós confiamos no nome do Senhor nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14191, 'nvi', 'Salmos', 'sl', 20, 8, 'Eles vacilam e caem, mas nós nos erguemos e estamos firmes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14192, 'nvi', 'Salmos', 'sl', 20, 9, 'Senhor, concede vitória ao rei! Responde-nos quando clamamos!');
