INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14193, 'nvi', 'Salmos', 'sl', 21, 1, 'O rei se alegra na tua força, ó Senhor! Como é grande a sua exultação pelas vitórias que lhe dás!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14194, 'nvi', 'Salmos', 'sl', 21, 2, 'Tu lhe concedeste o desejo do seu coração e não lhe rejeitaste o pedido dos seus lábios. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14195, 'nvi', 'Salmos', 'sl', 21, 3, 'Tu o recebeste com ricas bênçãos, e em sua cabeça puseste uma coroa de ouro puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14196, 'nvi', 'Salmos', 'sl', 21, 4, 'Ele te pediu vida, e tu lhe deste! Vida longa e duradoura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14197, 'nvi', 'Salmos', 'sl', 21, 5, 'Pelas vitórias que lhe deste, grande é a sua glória; de esplendor e majestade o cobriste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14198, 'nvi', 'Salmos', 'sl', 21, 6, 'Fizeste dele uma grande bênção para sempre e lhe deste a alegria da tua presença.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14199, 'nvi', 'Salmos', 'sl', 21, 7, 'O rei confia no Senhor: por causa da fidelidade do Altíssimo ele não será abalado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14200, 'nvi', 'Salmos', 'sl', 21, 8, 'Tua mão alcançará todos os teus inimigos; tua mão direita atingirá todos os que te odeiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14201, 'nvi', 'Salmos', 'sl', 21, 9, 'No dia em que te manifestares farás deles uma fornalha ardente. Na sua ira o Senhor os devorará, um fogo os consumirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14202, 'nvi', 'Salmos', 'sl', 21, 10, 'Acabarás com a geração deles na terra, com a sua descendência entre os homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14203, 'nvi', 'Salmos', 'sl', 21, 11, 'Embora tramem o mal contra ti e façam planos perversos, nada conseguirão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14204, 'nvi', 'Salmos', 'sl', 21, 12, 'pois tu os porás em fuga quando apontares para eles o teu arco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14205, 'nvi', 'Salmos', 'sl', 21, 13, 'Sê exaltado, Senhor, na tua força! Cantaremos e louvaremos o teu poder.');
