INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14206, 'nvi', 'Salmos', 'sl', 22, 1, 'Meu Deus! Meu Deus! Por que me abandonaste? Por que estás tão longe de salvar-me, tão longe dos meus gritos de angústia?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14207, 'nvi', 'Salmos', 'sl', 22, 2, 'Meu Deus! Eu clamo de dia, mas não respondes; de noite, e não recebo alívio!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14208, 'nvi', 'Salmos', 'sl', 22, 3, 'Tu, porém, és o Santo, és rei, és o louvor de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14209, 'nvi', 'Salmos', 'sl', 22, 4, 'Em ti os nossos antepassados puseram a sua confiança; confiaram, e os livraste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14210, 'nvi', 'Salmos', 'sl', 22, 5, 'Clamaram a ti, e foram libertos; em ti confiaram, e não se decepcionaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14211, 'nvi', 'Salmos', 'sl', 22, 6, 'Mas eu sou verme, e não homem, motivo de zombaria e objeto de desprezo do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14212, 'nvi', 'Salmos', 'sl', 22, 7, 'Caçoam de mim todos os que me vêem; balançando a cabeça, lançam insultos contra mim, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14213, 'nvi', 'Salmos', 'sl', 22, 8, '"Recorra ao Senhor! Que o Senhor o liberte! Que ele o livre, já que lhe quer bem! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14214, 'nvi', 'Salmos', 'sl', 22, 9, 'Contudo, tu mesmo me tiraste do ventre; deste-me segurança junto ao seio de minha mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14215, 'nvi', 'Salmos', 'sl', 22, 10, 'Desde que nasci fui entregue a ti; desde o ventre materno és o meu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14216, 'nvi', 'Salmos', 'sl', 22, 11, 'Não fiques distante de mim, pois a angústia está perto e não há ninguém que me socorra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14217, 'nvi', 'Salmos', 'sl', 22, 12, 'Muitos touros me cercam, sim, rodeiam-me os poderosos de Basã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14218, 'nvi', 'Salmos', 'sl', 22, 13, 'Como leão voraz rugindo escancaram a boca contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14219, 'nvi', 'Salmos', 'sl', 22, 14, 'Como água me derramei, e todos os meus ossos estão desconjuntados. Meu coração se tornou como cera; derreteu-se no meu íntimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14220, 'nvi', 'Salmos', 'sl', 22, 15, 'Meu vigor secou-se como um caco de barro, e a minha língua gruda no céu da boca; deixaste-me no pó, à beira da morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14221, 'nvi', 'Salmos', 'sl', 22, 16, 'Cães me rodearam! Um bando de homens maus me cercou! Perfuraram minhas mãos e meus pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14222, 'nvi', 'Salmos', 'sl', 22, 17, 'Posso contar todos os meus ossos, mas eles me encaram com desprezo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14223, 'nvi', 'Salmos', 'sl', 22, 18, 'Dividiram as minhas roupas entre si, e tiraram sortes pelas minhas vestes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14224, 'nvi', 'Salmos', 'sl', 22, 19, 'Tu, porém, Senhor, não fiques distante! Ó minha força, vem logo em meu socorro!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14225, 'nvi', 'Salmos', 'sl', 22, 20, 'Livra-me da espada, livra a minha vida do ataque dos cães.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14226, 'nvi', 'Salmos', 'sl', 22, 21, 'Salva-me da boca dos leões, e dos chifres dos bois selvagens. E tu me respondeste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14227, 'nvi', 'Salmos', 'sl', 22, 22, 'Proclamarei o teu nome a meus irmãos; na assembléia te louvarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14228, 'nvi', 'Salmos', 'sl', 22, 23, 'Louvem-no, vocês que temem o Senhor! Glorifiquem-no, todos vocês, descendentes de Jacó! Tremam diante dele, todos vocês, descendentes de Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14229, 'nvi', 'Salmos', 'sl', 22, 24, 'Pois não menosprezou nem repudiou o sofrimento do aflito; não escondeu dele o rosto, mas ouviu o seu grito de socorro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14230, 'nvi', 'Salmos', 'sl', 22, 25, 'De ti vem o tema do meu louvor na grande assembléia; na presença dos que te temem cumprirei os meus votos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14231, 'nvi', 'Salmos', 'sl', 22, 26, 'Os pobres comerão até ficarem satisfeitos; aqueles que buscam o Senhor o louvarão! Que vocês tenham vida longa!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14232, 'nvi', 'Salmos', 'sl', 22, 27, 'Todos os confins da terra se lembrarão e se voltarão para o Senhor, e todas as famílias das nações se prostrarão diante dele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14233, 'nvi', 'Salmos', 'sl', 22, 28, 'pois do Senhor é o reino; ele governa as nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14234, 'nvi', 'Salmos', 'sl', 22, 29, 'Todos os ricos da terra se banquetearão e o adorarão; haverão de ajoelhar-se diante dele todos os que descem ao pó, cuja vida se esvai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14235, 'nvi', 'Salmos', 'sl', 22, 30, 'A posteridade o servirá; gerações futuras ouvirão falar do Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14236, 'nvi', 'Salmos', 'sl', 22, 31, 'e a um povo que ainda não nasceu proclamarão seus feitos de justiça, pois ele agiu poderosamente.');
