INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14237, 'nvi', 'Salmos', 'sl', 23, 1, 'O Senhor é o meu pastor; de nada terei falta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14238, 'nvi', 'Salmos', 'sl', 23, 2, 'Em verdes pastagens me faz repousar e me conduz a águas tranqüilas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14239, 'nvi', 'Salmos', 'sl', 23, 3, 'restaura-me o vigor. Guia-me nas veredas da justiça por amor do seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14240, 'nvi', 'Salmos', 'sl', 23, 4, 'Mesmo quando eu andar por um vale de trevas e morte, não temerei perigo algum, pois tu estás comigo; a tua vara e o teu cajado me protegem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14241, 'nvi', 'Salmos', 'sl', 23, 5, 'Preparas um banquete para mim à vista dos meus inimigos. Tu me honras, ungindo a minha cabeça com óleo e fazendo transbordar o meu cálice.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14242, 'nvi', 'Salmos', 'sl', 23, 6, 'Sei que a bondade e a fidelidade me acompanharão todos os dias da minha vida, e voltarei à casa do Senhor enquanto eu viver.');
