INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14243, 'nvi', 'Salmos', 'sl', 24, 1, 'Do Senhor é a terra e tudo o que nela existe, o mundo e os que nele vivem;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14244, 'nvi', 'Salmos', 'sl', 24, 2, 'pois foi ele quem fundou-a sobre os mares e firmou-a sobre as águas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14245, 'nvi', 'Salmos', 'sl', 24, 3, 'Quem poderá subir o monte do Senhor? Quem poderá entrar no seu Santo Lugar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14246, 'nvi', 'Salmos', 'sl', 24, 4, 'Aquele que tem as mãos limpas e o coração puro, que não recorre aos ídolos nem jura por deuses falsos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14247, 'nvi', 'Salmos', 'sl', 24, 5, 'Ele receberá bênçãos do Senhor, e Deus, o seu Salvador lhe fará justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14248, 'nvi', 'Salmos', 'sl', 24, 6, 'São assim aqueles que o buscam, que buscam a tua face, ó Deus de Jacó. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14249, 'nvi', 'Salmos', 'sl', 24, 7, 'Abram-se, ó portais; abram-se, ó portas antigas, para que o Rei da glória entre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14250, 'nvi', 'Salmos', 'sl', 24, 8, 'Quem é o Rei da glória? O Senhor forte e valente, o Senhor valente nas guerras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14251, 'nvi', 'Salmos', 'sl', 24, 9, 'Abram-se, ó portais; abram-se, ó portas antigas, para que o Rei da glória entre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14252, 'nvi', 'Salmos', 'sl', 24, 10, 'Quem é esse Rei da glória? O Senhor dos Exércitos; ele é o Rei da glória! Pausa');
