INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14275, 'nvi', 'Salmos', 'sl', 26, 1, 'Faze-me justiça, Senhor, pois tenho vivido com integridade. Tenho confiado no Senhor, sem vacilação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14276, 'nvi', 'Salmos', 'sl', 26, 2, 'Sonda-me, Senhor, e prova-me, examina o meu coração e a minha mente;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14277, 'nvi', 'Salmos', 'sl', 26, 3, 'pois o teu amor está sempre diante de mim, e continuamente sigo a tua verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14278, 'nvi', 'Salmos', 'sl', 26, 4, 'Não me associo com homens falsos, nem ando com hipócritas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14279, 'nvi', 'Salmos', 'sl', 26, 5, 'detesto o ajuntamento dos malfeitores, e não me assento com os ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14280, 'nvi', 'Salmos', 'sl', 26, 6, 'Lavo as mãos na inocência, e do teu altar, Senhor, me aproximo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14281, 'nvi', 'Salmos', 'sl', 26, 7, 'cantando hinos de gratidão e falando de todas as tuas maravilhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14282, 'nvi', 'Salmos', 'sl', 26, 8, 'Eu amo, Senhor, o lugar da tua habitação, onde a tua glória habita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14283, 'nvi', 'Salmos', 'sl', 26, 9, 'Não me dês o destino dos pecadores, nem o fim dos assassinos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14284, 'nvi', 'Salmos', 'sl', 26, 10, 'suas mãos executam planos perversos, praticam suborno abertamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14285, 'nvi', 'Salmos', 'sl', 26, 11, 'Mas eu vivo com integridade; livra-me e tem misericórdia de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14286, 'nvi', 'Salmos', 'sl', 26, 12, 'Os meus pés estão firmes na retidão; na grande assembléia bendirei o Senhor.');
