INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14310, 'nvi', 'Salmos', 'sl', 29, 1, 'Atribuam ao Senhor, ó seres celestiais, atribuam ao Senhor glória e força.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14311, 'nvi', 'Salmos', 'sl', 29, 2, 'Atribuam ao Senhor a glória que o seu nome merece; adorem o Senhor no esplendor do seu santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14312, 'nvi', 'Salmos', 'sl', 29, 3, 'A voz do Senhor ressoa sobre as águas; o Deus da glória troveja, o Senhor troveja sobre as muitas águas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14313, 'nvi', 'Salmos', 'sl', 29, 4, 'A voz do Senhor é poderosa; a voz do Senhor é majestosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14314, 'nvi', 'Salmos', 'sl', 29, 5, 'A voz do Senhor quebra os cedros; o Senhor despedaça os cedros do Líbano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14315, 'nvi', 'Salmos', 'sl', 29, 6, 'Ele faz o Líbano saltar como bezerro, o Siriom como novilho selvagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14316, 'nvi', 'Salmos', 'sl', 29, 7, 'A voz do Senhor corta os céus com raios flamejantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14317, 'nvi', 'Salmos', 'sl', 29, 8, 'A voz do Senhor faz tremer o deserto; o Senhor faz tremer o deserto de Cades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14318, 'nvi', 'Salmos', 'sl', 29, 9, 'A voz do Senhor retorce os carvalhos e despe as florestas. E no seu templo todos clamam: "Glória! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14319, 'nvi', 'Salmos', 'sl', 29, 10, 'O Senhor assentou-se soberano sobre o dilúvio; o Senhor reina soberano para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14320, 'nvi', 'Salmos', 'sl', 29, 11, 'O Senhor dá força ao seu povo; o Senhor dá a seu povo a bênção da paz.');
