INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13959, 'nvi', 'Salmos', 'sl', 3, 1, 'Senhor, muitos são os meus adversários! Muitos se rebelam contra mim!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13960, 'nvi', 'Salmos', 'sl', 3, 2, 'São muitos os que dizem a meu respeito: "Deus nunca o salvará! " Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13961, 'nvi', 'Salmos', 'sl', 3, 3, 'Mas tu, Senhor, és o escudo que me protege; és a minha glória e me fazes andar de cabeça erguida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13962, 'nvi', 'Salmos', 'sl', 3, 4, 'Ao Senhor clamo em alta voz, e do seu santo monte ele me responde. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13963, 'nvi', 'Salmos', 'sl', 3, 5, 'Eu me deito e durmo, e torno a acordar, porque é o Senhor que me sustém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13964, 'nvi', 'Salmos', 'sl', 3, 6, 'Não me assustam os milhares que me cercam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13965, 'nvi', 'Salmos', 'sl', 3, 7, 'Levanta-te, Senhor! Salva-me, Deus meu! Quebra o queixo de todos os meus inimigos; arrebenta os dentes dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13966, 'nvi', 'Salmos', 'sl', 3, 8, 'Do Senhor vem o livramento. A tua bênção está sobre o teu povo. Pausa');
