INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14321, 'nvi', 'Salmos', 'sl', 30, 1, 'Eu te exaltarei, Senhor, pois tu me reergueste e não deixaste que os meus inimigos se divertissem à minha custa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14322, 'nvi', 'Salmos', 'sl', 30, 2, 'Senhor meu Deus, a ti clamei por socorro, e tu me curaste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14323, 'nvi', 'Salmos', 'sl', 30, 3, 'Senhor, tiraste-me da sepultura; prestes a descer à cova, devolveste-me à vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14324, 'nvi', 'Salmos', 'sl', 30, 4, 'Cantem louvores ao Senhor, vocês, os seus fiéis; louvem o seu santo nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14325, 'nvi', 'Salmos', 'sl', 30, 5, 'Pois a sua ira só dura um instante, mas o seu favor dura a vida toda; o choro pode persistir uma noite, mas de manhã irrompe a alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14326, 'nvi', 'Salmos', 'sl', 30, 6, 'Quando me senti seguro, disse: "Jamais serei abalado! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14327, 'nvi', 'Salmos', 'sl', 30, 7, 'Senhor, com o teu favor, deste-me firmeza e estabilidade; mas, quando escondeste a tua face, fiquei aterrorizado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14328, 'nvi', 'Salmos', 'sl', 30, 8, 'A ti, Senhor, clamei, ao Senhor pedi misericórdia:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14329, 'nvi', 'Salmos', 'sl', 30, 9, '"Se eu morrer, se eu descer à cova, que vantagem haverá? Acaso o pó te louvará? Proclamará a tua fidelidade?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14330, 'nvi', 'Salmos', 'sl', 30, 10, 'Ouve, Senhor, e tem misericórdia de mim; Senhor, sê tu o meu auxílio".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14331, 'nvi', 'Salmos', 'sl', 30, 11, 'Mudaste o meu pranto em dança, a minha veste de lamento em veste de alegria,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14332, 'nvi', 'Salmos', 'sl', 30, 12, 'para que o meu coração cante louvores a ti e não se cale. Senhor, meu Deus, eu te darei graças para sempre.');
