INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14333, 'nvi', 'Salmos', 'sl', 31, 1, 'Em ti, Senhor, me refugio; nunca permitas que eu seja humilhado; livra-me pela tua justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14334, 'nvi', 'Salmos', 'sl', 31, 2, 'Inclina os teus ouvidos para mim, vem livrar-me depressa! Sê minha rocha de refúgio, uma fortaleza poderosa para me salvar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14335, 'nvi', 'Salmos', 'sl', 31, 3, 'Sim, tu és a minha rocha e a minha fortaleza; por amor do teu nome, conduze-me e guia-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14336, 'nvi', 'Salmos', 'sl', 31, 4, 'Tira-me da armadilha que me prepararam, pois tu és o meu refúgio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14337, 'nvi', 'Salmos', 'sl', 31, 5, 'Nas tuas mãos entrego o meu espírito; resgata-me, Senhor, Deus da verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14338, 'nvi', 'Salmos', 'sl', 31, 6, 'Odeio aqueles que se apegam a ídolos inúteis; eu, porém, confio no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14339, 'nvi', 'Salmos', 'sl', 31, 7, 'Exultarei com grande alegria por teu amor, pois viste a minha aflição e conheceste a angústia da minha alma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14340, 'nvi', 'Salmos', 'sl', 31, 8, 'Não me entregaste nas mãos dos meus inimigos; deste-me segurança e liberdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14341, 'nvi', 'Salmos', 'sl', 31, 9, 'Misericórdia, Senhor! Estou em desespero! A tristeza me consome a vista, o vigor e o apetite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14342, 'nvi', 'Salmos', 'sl', 31, 10, 'Minha vida é consumida pela angústia, e os meus anos pelo gemido; falta-me a força devido à minha aflição, e os meus ossos se enfraquecem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14343, 'nvi', 'Salmos', 'sl', 31, 11, 'Por causa de todos os meus adversários, sou motivo de ultraje para os meus vizinhos e de medo para os meus amigos; os que me vêem na rua fogem de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14344, 'nvi', 'Salmos', 'sl', 31, 12, 'Sou esquecido por eles como se estivesse morto; tornei-me como um pote quebrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14345, 'nvi', 'Salmos', 'sl', 31, 13, 'Ouço muitos cochicharem a meu respeito; o pavor me domina, pois conspiram contra mim, tramando tirar-me a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14346, 'nvi', 'Salmos', 'sl', 31, 14, 'Mas eu confio em ti, Senhor, e digo: "Tu és o meu Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14347, 'nvi', 'Salmos', 'sl', 31, 15, 'O meu futuro está nas tuas mãos; livra-me dos meus inimigos e daqueles que me perseguem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14348, 'nvi', 'Salmos', 'sl', 31, 16, 'Faze o teu rosto resplandecer sobre o teu servo; salva-me por teu amor leal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14349, 'nvi', 'Salmos', 'sl', 31, 17, 'Não permitas que eu seja humilhado, Senhor, pois tenho clamado a ti; mas que os ímpios sejam humilhados e calados fiquem no Sheol.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14350, 'nvi', 'Salmos', 'sl', 31, 18, 'Sejam emudecidos os seus lábios mentirosos, pois com arrogância e desprezo humilham os justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14351, 'nvi', 'Salmos', 'sl', 31, 19, 'Como é grande a tua bondade, que reservaste para aqueles que te temem, e que, à vista dos homens, concedes àqueles que se refugiam em ti!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14352, 'nvi', 'Salmos', 'sl', 31, 20, 'No abrigo da tua presença os escondes das intrigas dos homens; na tua habitação os proteges das línguas acusadoras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14353, 'nvi', 'Salmos', 'sl', 31, 21, 'Bendito seja o Senhor, pois mostrou o seu maravilhoso amor para comigo quando eu estava numa cidade cercada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14354, 'nvi', 'Salmos', 'sl', 31, 22, 'Alarmado, eu disse: "Fui excluído da tua presença! " Contudo, ouviste as minhas súplicas quando clamei a ti por socorro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14355, 'nvi', 'Salmos', 'sl', 31, 23, 'Amem o Senhor, todos vocês, os seus santos! O Senhor preserva os fiéis, mas aos arrogantes dá o que merecem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14356, 'nvi', 'Salmos', 'sl', 31, 24, 'Sejam fortes e corajosos, todos vocês que esperam no Senhor!');
