INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14412, 'nvi', 'Salmos', 'sl', 35, 1, 'Defende-me, Senhor, dos que me acusam; luta contra os que lutam comigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14413, 'nvi', 'Salmos', 'sl', 35, 2, 'Toma os escudos, o grande e o pequeno; levanta-te e vem socorrer-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14414, 'nvi', 'Salmos', 'sl', 35, 3, 'Empunha a lança e o machado de guerra contra os meus perseguidores. Dize à minha alma: "Eu sou a sua salvação".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14415, 'nvi', 'Salmos', 'sl', 35, 4, 'Sejam humilhados e desprezados os que procuram matar-me; retrocedam envergonhados aqueles que tramam a minha ruína.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14416, 'nvi', 'Salmos', 'sl', 35, 5, 'Que eles sejam como a palha ao vento, quando o anjo do Senhor os expulsar;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14417, 'nvi', 'Salmos', 'sl', 35, 6, 'seja a vereda deles sombria e escorregadia, quando o anjo do Senhor os perseguir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14418, 'nvi', 'Salmos', 'sl', 35, 7, 'Já que, sem motivo, prepararam contra mim uma armadilha oculta e, sem motivo, abriram uma cova para mim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14419, 'nvi', 'Salmos', 'sl', 35, 8, 'que a ruína lhes sobrevenha de surpresa: sejam presos pela armadilha que prepararam, caiam na cova que abriram, para a sua própria ruína.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14420, 'nvi', 'Salmos', 'sl', 35, 9, 'Então a minha alma exultará no Senhor e se regozijará na sua salvação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14421, 'nvi', 'Salmos', 'sl', 35, 10, 'Todo o meu ser exclamará: "Quem se compara a ti, Senhor? Tu livras os necessitados daqueles que são mais poderosos do que eles, livras os necessitados e os pobres daqueles que os exploram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14422, 'nvi', 'Salmos', 'sl', 35, 11, 'Testemunhas maldosas enfrentam-me e questionam-me sobre coisas de que nada sei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14423, 'nvi', 'Salmos', 'sl', 35, 12, 'Elas me retribuem o bem com o mal e procuram tirar-me a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14424, 'nvi', 'Salmos', 'sl', 35, 13, 'Contudo, quando estavam doentes, usei vestes de lamento, humilhei-me com jejum e recolhi-me em oração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14425, 'nvi', 'Salmos', 'sl', 35, 14, 'Saí vagueando e pranteando, como por um amigo ou por um irmão. Eu me prostrei enlutado, como quem lamenta por sua mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14426, 'nvi', 'Salmos', 'sl', 35, 15, 'Mas, quando tropecei, eles se reuniram alegres; sem que eu o soubesse, ajuntaram-se para me atacar. Eles me agrediram sem cessar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14427, 'nvi', 'Salmos', 'sl', 35, 16, 'Como ímpios caçoando do meu refúgio, rosnaram contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14428, 'nvi', 'Salmos', 'sl', 35, 17, 'Senhor, até quando ficarás olhando? Livra-me dos ataques deles, livra a minha vida preciosa desses leões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14429, 'nvi', 'Salmos', 'sl', 35, 18, 'Eu te darei graças na grande assembléia; no meio da grande multidão te louvarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14430, 'nvi', 'Salmos', 'sl', 35, 19, 'Não deixes que os meus inimigos traiçoeiros se divirtam à minha custa; não permitas que aqueles que sem razão me odeiam troquem olhares de desprezo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14431, 'nvi', 'Salmos', 'sl', 35, 20, 'Não falam pacificamente, mas planejam acusações falsas contra os que vivem tranqüilamente na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14432, 'nvi', 'Salmos', 'sl', 35, 21, 'Com a boca escancarada, riem de mim e me acusam: "Nós vimos! Sabemos de tudo! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14433, 'nvi', 'Salmos', 'sl', 35, 22, 'Tu viste isso, Senhor! Não fiques calado. Não te afastes de mim, Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14434, 'nvi', 'Salmos', 'sl', 35, 23, 'Acorda! Desperta! Faze-me justiça! Defende a minha causa, meu Deus e Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14435, 'nvi', 'Salmos', 'sl', 35, 24, 'Senhor, meu Deus, tu és justo; faze-me justiça para que eles não se alegrem à minha custa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14436, 'nvi', 'Salmos', 'sl', 35, 25, 'Não deixes que pensem: "Ah! Era isso que queríamos! ", nem que digam: "Acabamos com ele! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14437, 'nvi', 'Salmos', 'sl', 35, 26, 'Sejam humilhados e frustrados todos os que se divertem à custa do meu sofrimento; cubram-se de vergonha e desonra todos os que se acham superiores a mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14438, 'nvi', 'Salmos', 'sl', 35, 27, 'Cantem de alegria e regozijo todos os que desejam ver provada a minha inocência, e sempre repitam: "O Senhor seja engrandecido! Ele tem prazer no bem-estar do seu servo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14439, 'nvi', 'Salmos', 'sl', 35, 28, 'Minha língua proclamará a tua justiça e o teu louvor o dia inteiro.');
