INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14440, 'nvi', 'Salmos', 'sl', 36, 1, 'Há no meu íntimo um oráculo a respeito da maldade do ímpio: Aos seus olhos é inútil temer a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14441, 'nvi', 'Salmos', 'sl', 36, 2, 'Ele se acha tão importante, que não percebe nem rejeita o seu pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14442, 'nvi', 'Salmos', 'sl', 36, 3, 'As palavras da sua boca são maldosas e traiçoeiras; abandonou o bom senso e não quer fazer o bem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14443, 'nvi', 'Salmos', 'sl', 36, 4, 'Até na sua cama planeja maldade; nada há de bom no caminho a que se entregou, e ele nunca rejeita o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14444, 'nvi', 'Salmos', 'sl', 36, 5, 'O teu amor, Senhor, chega até os céus; a tua fidelidade até as nuvens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14445, 'nvi', 'Salmos', 'sl', 36, 6, 'A tua justiça é firme como as altas montanhas; as tuas decisões insondáveis como o grande mar. Tu, Senhor, preservas tanto os homens quanto os animais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14446, 'nvi', 'Salmos', 'sl', 36, 7, 'Como é precioso o teu amor, ó Deus! Os homens encontram refúgio à sombra das tuas asas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14447, 'nvi', 'Salmos', 'sl', 36, 8, 'Eles se banqueteiam na fartura da tua casa; tu lhes dás de beber do teu rio de delícias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14448, 'nvi', 'Salmos', 'sl', 36, 9, 'Pois em ti está a fonte da vida; graças à tua luz, vemos a luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14449, 'nvi', 'Salmos', 'sl', 36, 10, 'Estende o teu amor aos que te conhecem, a tua justiça aos que são retos de coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14450, 'nvi', 'Salmos', 'sl', 36, 11, 'Não permitas que o arrogante me pisoteie, nem que a mão do ímpio me faça recuar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14451, 'nvi', 'Salmos', 'sl', 36, 12, 'Lá estão os malfeitores caídos, lançados ao chão, incapazes de levantar-se!');
