INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13967, 'nvi', 'Salmos', 'sl', 4, 1, 'Responde-me quando clamo, ó Deus que me faz justiça! Dá-me alívio da minha angústia; Tem misericórdia de mim e ouve a minha oração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13968, 'nvi', 'Salmos', 'sl', 4, 2, 'Até quando vocês, ó poderosos, ultrajarão a minha honra? Até quando estarão amando ilusões e buscando mentiras? Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13969, 'nvi', 'Salmos', 'sl', 4, 3, 'Saibam que o Senhor escolheu o piedoso; o Senhor ouvirá quando eu o invocar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13970, 'nvi', 'Salmos', 'sl', 4, 4, 'Quando vocês ficarem irados, não pequem; ao deitar-se reflitam nisso, e aquietem-se. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13971, 'nvi', 'Salmos', 'sl', 4, 5, 'Ofereçam sacrifícios como Deus exige e confiem no Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13972, 'nvi', 'Salmos', 'sl', 4, 6, 'Muitos perguntam: "Quem nos fará desfrutar o bem? " Faze, ó Senhor, resplandecer sobre nós a luz do teu rosto!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13973, 'nvi', 'Salmos', 'sl', 4, 7, 'Encheste o meu coração de alegria, alegria maior do que a daqueles que têm fartura de trigo e de vinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13974, 'nvi', 'Salmos', 'sl', 4, 8, 'Em paz me deito e logo adormeço, pois só tu, Senhor, me fazes viver em segurança.');
