INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14544, 'nvi', 'Salmos', 'sl', 41, 1, 'Como é feliz aquele que se interessa pelo pobre! O Senhor o livra em tempos de adversidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14545, 'nvi', 'Salmos', 'sl', 41, 2, 'O Senhor o protegerá e preservará a sua vida; ele o fará feliz na terra e não o entregará ao desejo dos seus inimigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14546, 'nvi', 'Salmos', 'sl', 41, 3, 'O Senhor o susterá em seu leito de enfermidade, e da doença o restaurará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14547, 'nvi', 'Salmos', 'sl', 41, 4, 'Eu disse: Misericórdia, Senhor, cura-me, pois pequei contra ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14548, 'nvi', 'Salmos', 'sl', 41, 5, 'Os meus inimigos dizem maldosamente a meu respeito: "Quando ele vai morrer? Quando vai desaparecer o seu nome? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14549, 'nvi', 'Salmos', 'sl', 41, 6, 'Sempre que alguém vem visitar-me, fala com falsidade, enche o coração de calúnias e depois sai espalhando-as.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14550, 'nvi', 'Salmos', 'sl', 41, 7, 'Todos os que me odeiam juntam-se e cochicham contra mim, imaginando que o pior me acontecerá:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14551, 'nvi', 'Salmos', 'sl', 41, 8, '"Uma praga terrível o derrubou; está de cama, e jamais se levantará".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14552, 'nvi', 'Salmos', 'sl', 41, 9, 'Até o meu melhor amigo, em quem eu confiava e que partilhava do meu pão, voltou-se contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14553, 'nvi', 'Salmos', 'sl', 41, 10, 'Mas, tu, Senhor, tem misericórdia de mim; levanta-me, para que eu lhes retribua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14554, 'nvi', 'Salmos', 'sl', 41, 11, 'Sei que me queres bem, pois o meu inimigo não triunfa sobre mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14555, 'nvi', 'Salmos', 'sl', 41, 12, 'Por causa da minha integridade me susténs e me pões na tua presença para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14556, 'nvi', 'Salmos', 'sl', 41, 13, 'Louvado seja o Senhor, o Deus de Israel, de eternidade a eternidade! Amém e amém!');
