INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14616, 'nvi', 'Salmos', 'sl', 46, 1, 'Deus é o nosso refúgio e a nossa fortaleza, auxílio sempre presente na adversidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14617, 'nvi', 'Salmos', 'sl', 46, 2, 'Por isso não temeremos, embora a terra trema e os montes afundem no coração do mar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14618, 'nvi', 'Salmos', 'sl', 46, 3, 'embora estrondem as suas águas turbulentas e os montes sejam sacudidos pela sua fúria. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14619, 'nvi', 'Salmos', 'sl', 46, 4, 'Há um rio cujos canais alegram a cidade de Deus, o Santo Lugar onde habita o Altíssimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14620, 'nvi', 'Salmos', 'sl', 46, 5, 'Deus nela está! Não será abalada! Deus vem em seu auxílio desde o romper da manhã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14621, 'nvi', 'Salmos', 'sl', 46, 6, 'Nações se agitam, reinos se abalam; ele ergue a voz, e a terra se derrete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14622, 'nvi', 'Salmos', 'sl', 46, 7, 'O Senhor dos Exércitos está conosco; o Deus de Jacó é a nossa torre segura. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14623, 'nvi', 'Salmos', 'sl', 46, 8, 'Venham! Vejam as obras do Senhor, seus feitos estarrecedores na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14624, 'nvi', 'Salmos', 'sl', 46, 9, 'Ele dá fim às guerras até os confins da terra; quebra o arco e despedaça a lança, destrói os escudos com fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14625, 'nvi', 'Salmos', 'sl', 46, 10, '"Parem de lutar! Saibam que eu sou Deus! Serei exaltado entre as nações, serei exaltado na terra. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14626, 'nvi', 'Salmos', 'sl', 46, 11, 'O Senhor dos Exércitos está conosco; o Deus de Jacó é a nossa torre segura. Pausa');
