INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14627, 'nvi', 'Salmos', 'sl', 47, 1, 'Batam palmas, vocês, todos os povos; aclamem a Deus com cantos de alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14628, 'nvi', 'Salmos', 'sl', 47, 2, 'Pois o Senhor Altíssimo é temível, é o grande Rei sobre toda a terra!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14629, 'nvi', 'Salmos', 'sl', 47, 3, 'Ele subjugou as nações ao nosso poder, os povos colocou debaixo de nossos pés,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14630, 'nvi', 'Salmos', 'sl', 47, 4, 'e escolheu para nós a nossa herança, o orgulho de Jacó, a quem amou. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14631, 'nvi', 'Salmos', 'sl', 47, 5, 'Deus subiu em meio a gritos de alegria; o Senhor, em meio ao som de trombetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14632, 'nvi', 'Salmos', 'sl', 47, 6, 'Ofereçam música a Deus, cantem louvores! Ofereçam música ao nosso Rei, cantem louvores!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14633, 'nvi', 'Salmos', 'sl', 47, 7, 'Pois Deus é o rei de toda a terra; cantem louvores com harmonia e arte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14634, 'nvi', 'Salmos', 'sl', 47, 8, 'Deus reina sobre as nações; Deus está assentado em seu santo trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14635, 'nvi', 'Salmos', 'sl', 47, 9, 'Os soberanos das nações se juntam ao povo do Deus de Abraão, pois os governantes da terra pertencem a Deus; ele é soberanamente exaltado.');
