INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14636, 'nvi', 'Salmos', 'sl', 48, 1, 'Grande é o Senhor, e digno de todo louvor na cidade do nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14637, 'nvi', 'Salmos', 'sl', 48, 2, 'Seu santo monte, belo e majestoso, é a alegria da terra toda. Como as alturas do Zafom é o monte Sião, a cidade do grande Rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14638, 'nvi', 'Salmos', 'sl', 48, 3, 'Nas suas cidadelas Deus se revela como sua proteção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14639, 'nvi', 'Salmos', 'sl', 48, 4, 'Vejam! Os reis somaram forças, e juntos avançaram contra ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14640, 'nvi', 'Salmos', 'sl', 48, 5, 'Quando a viram, ficaram atônitos, fugiram aterrorizados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14641, 'nvi', 'Salmos', 'sl', 48, 6, 'Ali mesmo o pavor os dominou; contorceram-se como a mulher no parto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14642, 'nvi', 'Salmos', 'sl', 48, 7, 'Foste como o vento oriental quando destruiu os navios de Társis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14643, 'nvi', 'Salmos', 'sl', 48, 8, 'Como já temos ouvido, agora também temos visto na cidade do Senhor dos Exércitos, na cidade de nosso Deus: Deus a preserva firme para sempre. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14644, 'nvi', 'Salmos', 'sl', 48, 9, 'No teu templo, ó Deus, meditamos em teu amor leal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14645, 'nvi', 'Salmos', 'sl', 48, 10, 'Como o teu nome, ó Deus, o teu louvor alcança os confins da terra; a tua mão direita está cheia de justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14646, 'nvi', 'Salmos', 'sl', 48, 11, 'O monte Sião se alegra, as cidades de Judá exultam por causa das tuas decisões justas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14647, 'nvi', 'Salmos', 'sl', 48, 12, 'Percorram Sião, contornando-a, contem as suas torres,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14648, 'nvi', 'Salmos', 'sl', 48, 13, 'observem bem as suas muralhas, examinem as suas cidadelas, para que vocês falem à próxima geração');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14649, 'nvi', 'Salmos', 'sl', 48, 14, 'que este Deus é o nosso Deus para todo o sempre; ele será o nosso guia até o fim.');
