INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14712, 'nvi', 'Salmos', 'sl', 52, 1, 'Por que você se vangloria do mal e de ultrajar a Deus continuamente?, ó homem poderoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14713, 'nvi', 'Salmos', 'sl', 52, 2, 'Sua língua trama destruição; é como navalha afiada, cheia de engano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14714, 'nvi', 'Salmos', 'sl', 52, 3, 'Você prefere o mal ao bem, a falsidade, em lugar da verdade. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14715, 'nvi', 'Salmos', 'sl', 52, 4, 'Você ama toda palavra maldosa, ó língua mentirosa!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14716, 'nvi', 'Salmos', 'sl', 52, 5, 'Saiba que Deus o arruinará para sempre: Ele o agarrará e o arrancará da sua tenda; ele o desarraigará da terra dos vivos. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14717, 'nvi', 'Salmos', 'sl', 52, 6, 'Os justos verão isso e temerão; rirão dele, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14718, 'nvi', 'Salmos', 'sl', 52, 7, '"Veja só o homem que rejeitou a Deus como refúgio; confiou em sua grande riqueza e buscou refúgio em sua maldade! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14719, 'nvi', 'Salmos', 'sl', 52, 8, 'Mas eu sou como uma oliveira que floresce na casa de Deus; confio no amor de Deus para todo o sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14720, 'nvi', 'Salmos', 'sl', 52, 9, 'Para sempre te louvarei pelo que fizeste; na presença dos teus fiéis proclamarei o teu nome, porque tu és bom.');
