INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14727, 'nvi', 'Salmos', 'sl', 54, 1, 'Salva-me, ó Deus, pelo teu nome; defende-me pelo teu poder.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14728, 'nvi', 'Salmos', 'sl', 54, 2, 'Ouve a minha oração, ó Deus; escuta as minhas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14729, 'nvi', 'Salmos', 'sl', 54, 3, 'Estrangeiros me atacam; homens cruéis querem matar-me, homens que não se importam com Deus. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14730, 'nvi', 'Salmos', 'sl', 54, 4, 'Certamente Deus é o meu auxílio; é o Senhor que me sustém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14731, 'nvi', 'Salmos', 'sl', 54, 5, 'Recaia o mal sobre os meus inimigos! Extermina-os por tua fidelidade!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14732, 'nvi', 'Salmos', 'sl', 54, 6, 'Eu te oferecerei um sacrifício voluntário; louvarei o teu nome, ó Senhor, porque tu és bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14733, 'nvi', 'Salmos', 'sl', 54, 7, 'Pois ele me livrou de todas as minhas angústias, e os meus olhos contemplaram a derrota dos meus inimigos.');
