INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14757, 'nvi', 'Salmos', 'sl', 56, 1, 'Tem misericórdia de mim, ó Deus, pois os homens me pressionam; o tempo todo me atacam e me oprimem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14758, 'nvi', 'Salmos', 'sl', 56, 2, 'Os meus inimigos pressionam-me sem parar; muitos atacam-me arrogantemente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14759, 'nvi', 'Salmos', 'sl', 56, 3, 'Mas eu, quando estiver com medo, confiarei em ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14760, 'nvi', 'Salmos', 'sl', 56, 4, 'Em Deus, cuja palavra eu louvo, em Deus eu confio, e não temerei. Que poderá fazer-me o simples mortal?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14761, 'nvi', 'Salmos', 'sl', 56, 5, 'O tempo todo eles distorcem as minhas palavras; estão sempre tramando prejudicar-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14762, 'nvi', 'Salmos', 'sl', 56, 6, 'Conspiram, ficam à espreita, vigiam os meus passos, na esperança de tirar-me a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14763, 'nvi', 'Salmos', 'sl', 56, 7, 'Deixarás escapar essa gente tão perversa? Na tua ira, ó Deus, derruba as nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14764, 'nvi', 'Salmos', 'sl', 56, 8, 'Registra, tu mesmo, o meu lamento; recolhe as minhas lágrimas em teu odre; acaso não estão anotadas em teu livro?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14765, 'nvi', 'Salmos', 'sl', 56, 9, 'Os meus inimigos retrocederão, quando eu clamar por socorro. Com isso saberei que Deus está a meu favor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14766, 'nvi', 'Salmos', 'sl', 56, 10, 'Confio em Deus, cuja palavra louvo, no Senhor, cuja palavra louvo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14767, 'nvi', 'Salmos', 'sl', 56, 11, 'nesse Deus eu confio, e não temerei. Que poderá fazer-me o homem?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14768, 'nvi', 'Salmos', 'sl', 56, 12, 'Cumprirei os votos que te fiz, ó Deus; a ti apresentarei minhas ofertas de gratidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14769, 'nvi', 'Salmos', 'sl', 56, 13, 'Pois me livraste da morte e os meus pés de tropeçarem, para que eu ande diante de Deus na luz que ilumina os vivos.');
