INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14770, 'nvi', 'Salmos', 'sl', 57, 1, 'Misericórdia, ó Deus; misericórdia, pois em ti a minha alma se refugia. Eu me refugiarei à sombra das tuas asas, até que passe o perigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14771, 'nvi', 'Salmos', 'sl', 57, 2, 'Clamo ao Deus Altíssimo, a Deus, que para comigo cumpre o seu propósito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14772, 'nvi', 'Salmos', 'sl', 57, 3, 'Dos céus ele me envia a salvação, põe em fuga os que me perseguem de perto; Pausa Deus envia o seu amor e a sua fidelidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14773, 'nvi', 'Salmos', 'sl', 57, 4, 'Estou em meio a leões, ávidos para devorar; seus dentes são lanças e flechas, suas línguas são espadas afiadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14774, 'nvi', 'Salmos', 'sl', 57, 5, 'Sê exaltado, ó Deus, acima dos céus! Sobre toda a terra esteja a tua glória!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14775, 'nvi', 'Salmos', 'sl', 57, 6, 'Preparam armadilhas para os meus pés; fiquei muito abatido. Abriram uma cova no meu caminho, mas foram eles que nela caíram. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14776, 'nvi', 'Salmos', 'sl', 57, 7, 'Meu coração está firme, ó Deus, meu coração está firme; cantarei ao som de instrumentos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14777, 'nvi', 'Salmos', 'sl', 57, 8, 'Acorde, minha alma! Acordem, harpa e lira! Vou despertar a alvorada!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14778, 'nvi', 'Salmos', 'sl', 57, 9, 'Eu te louvarei, ó Senhor, entre as nações; cantarei teus louvores entre os povos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14779, 'nvi', 'Salmos', 'sl', 57, 10, 'Pois o teu amor é tão grande que alcança os céus; a tua fidelidade vai até às nuvens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14780, 'nvi', 'Salmos', 'sl', 57, 11, 'Sê exaltado, ó Deus, acima dos céus! Sobre toda a terra esteja a tua glória!');
