INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14781, 'nvi', 'Salmos', 'sl', 58, 1, 'Será que vocês, poderosos, falam de fato com justiça? Será que vocês, homens, julgam retamente?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14782, 'nvi', 'Salmos', 'sl', 58, 2, 'Não! No coração vocês tramam a injustiça, e na terra as suas mãos espalham a violência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14783, 'nvi', 'Salmos', 'sl', 58, 3, 'Os ímpios erram o caminho desde o ventre; desviam-se os mentirosos desde que nascem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14784, 'nvi', 'Salmos', 'sl', 58, 4, 'Seu veneno é como veneno de serpente; tapam os ouvidos, como a cobra que se faz de surda');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14785, 'nvi', 'Salmos', 'sl', 58, 5, 'para não ouvir a música dos encantadores, que fazem encantamentos com tanta habilidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14786, 'nvi', 'Salmos', 'sl', 58, 6, 'Quebra os dentes deles, ó Deus; arranca, Senhor, as presas desses leões!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14787, 'nvi', 'Salmos', 'sl', 58, 7, 'Desapareçam como a água que escorre! Quando empunharem o arco, caiam sem força as suas flechas!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14788, 'nvi', 'Salmos', 'sl', 58, 8, 'Sejam como a lesma que se derrete pelo caminho; como feto abortado, não vejam eles o sol!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14789, 'nvi', 'Salmos', 'sl', 58, 9, 'Os ímpios serão varridos antes que as suas panelas sintam o calor da lenha, esteja ela verde ou seca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14790, 'nvi', 'Salmos', 'sl', 58, 10, 'Os justos se alegrarão quando forem vingados, quando banharem seus pés no sangue dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14791, 'nvi', 'Salmos', 'sl', 58, 11, 'Então os homens comentarão: "De fato os justos têm a sua recompensa; com certeza há um Deus que faz justiça na terra".');
