INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13987, 'nvi', 'Salmos', 'sl', 6, 1, 'Senhor, não me castigues na tua ira nem me disciplines no teu furor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13988, 'nvi', 'Salmos', 'sl', 6, 2, 'Misericórdia, Senhor, pois vou desfalecendo! Cura-me, Senhor, pois os meus ossos tremem:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13989, 'nvi', 'Salmos', 'sl', 6, 3, 'Todo o meu ser estremece. Até quando, Senhor, até quando?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13990, 'nvi', 'Salmos', 'sl', 6, 4, 'Volta-te, Senhor, e livra-me; salva-me por causa do teu amor leal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13991, 'nvi', 'Salmos', 'sl', 6, 5, 'Quem morreu não se lembra de ti. Entre os mortos, quem te louvará?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13992, 'nvi', 'Salmos', 'sl', 6, 6, 'Estou exausto de tanto gemer. De tanto chorar inundo de noite a minha cama; de lágrimas encharco o meu leito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13993, 'nvi', 'Salmos', 'sl', 6, 7, 'Os meus olhos se consomem de tristeza; fraquejam por causa de todos os meus adversários.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13994, 'nvi', 'Salmos', 'sl', 6, 8, 'Afastem-se de mim todos vocês que praticam o mal, porque o Senhor ouviu o meu choro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13995, 'nvi', 'Salmos', 'sl', 6, 9, 'O Senhor ouviu a minha súplica; o Senhor aceitou a minha oração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13996, 'nvi', 'Salmos', 'sl', 6, 10, 'Serão humilhados e aterrorizados todos os meus inimigos; frustrados, recuarão de repente.');
