INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14809, 'nvi', 'Salmos', 'sl', 60, 1, 'Tu nos rejeitaste e nos dispersaste, ó Deus; tu derramaste a tua ira; restaura-nos agora!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14810, 'nvi', 'Salmos', 'sl', 60, 2, 'Sacudiste a terra e abriste-lhe fendas; repara suas brechas, pois ameaça desmoronar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14811, 'nvi', 'Salmos', 'sl', 60, 3, 'Fizeste passar o teu povo por tempos difíceis; deste-nos um vinho estonteante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14812, 'nvi', 'Salmos', 'sl', 60, 4, 'Mas aos que te temem deste um sinal para que fugissem das flechas. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14813, 'nvi', 'Salmos', 'sl', 60, 5, 'Salva-nos com a tua mão direita e responde-nos, para que sejam libertos aqueles a quem amas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14814, 'nvi', 'Salmos', 'sl', 60, 6, 'Do seu santuário Deus falou: "No meu triunfo dividirei Siquém e repartirei o vale de Sucote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14815, 'nvi', 'Salmos', 'sl', 60, 7, 'Gileade é minha, Manassés também; Efraim é o meu capacete, Judá é o meu cetro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14816, 'nvi', 'Salmos', 'sl', 60, 8, 'Moabe é a pia em que me lavo, em Edom atiro a minha sandália; sobre a Filístia dou meu brado de vitória! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14817, 'nvi', 'Salmos', 'sl', 60, 9, 'Quem me levará à cidade fortificada? Quem me guiará a Edom?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14818, 'nvi', 'Salmos', 'sl', 60, 10, 'Não foste tu, ó Deus, que nos rejeitaste e deixaste de sair com os nossos exércitos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14819, 'nvi', 'Salmos', 'sl', 60, 11, 'Dá-nos ajuda contra os adversários, pois inútil é o socorro do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14820, 'nvi', 'Salmos', 'sl', 60, 12, 'Com Deus conquistaremos a vitória, e ele pisoteará os nossos adversários.');
