INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14821, 'nvi', 'Salmos', 'sl', 61, 1, 'Ouve o meu clamor, ó Deus; atenta para a minha oração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14822, 'nvi', 'Salmos', 'sl', 61, 2, 'Desde os confins da terra eu clamo a ti, com o coração abatido; põe-me à salvo na rocha mais alta do que eu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14823, 'nvi', 'Salmos', 'sl', 61, 3, 'Pois tu tens sido o meu refúgio, uma torre forte contra o inimigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14824, 'nvi', 'Salmos', 'sl', 61, 4, 'Para sempre anseio habitar na tua tenda e refugiar-me no abrigo das tuas asas. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14825, 'nvi', 'Salmos', 'sl', 61, 5, 'Pois ouviste os meus votos, ó Deus; deste-me a herança que concedes aos que temem o teu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14826, 'nvi', 'Salmos', 'sl', 61, 6, 'Prolonga os dias do rei, por muitas gerações os seus anos de vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14827, 'nvi', 'Salmos', 'sl', 61, 7, 'Para sempre esteja ele em seu trono, diante de Deus; envia o teu amor e a tua fidelidade para protegê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14828, 'nvi', 'Salmos', 'sl', 61, 8, 'Então sempre cantarei louvores ao teu nome, cumprindo os meus votos cada dia.');
