INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14841, 'nvi', 'Salmos', 'sl', 63, 1, 'Ó Deus, tu és o meu Deus, eu te busco intensamente; a minha alma tem sede de ti! Todo o meu ser anseia por ti, numa terra seca, exausta e sem água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14842, 'nvi', 'Salmos', 'sl', 63, 2, 'Quero contemplar-te no santuário e avistar o teu poder e a tua glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14843, 'nvi', 'Salmos', 'sl', 63, 3, 'O teu amor é melhor do que a vida! Por isso os meus lábios te exaltarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14844, 'nvi', 'Salmos', 'sl', 63, 4, 'Eu te bendirei enquanto viver, e em teu nome levantarei as minhas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14845, 'nvi', 'Salmos', 'sl', 63, 5, 'A minha alma ficará satisfeita como de rico banquete; com lábios jubilosos a minha boca te louvará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14846, 'nvi', 'Salmos', 'sl', 63, 6, 'Quando me deito lembro-me de ti; penso em ti durante as vigílias da noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14847, 'nvi', 'Salmos', 'sl', 63, 7, 'Porque és a minha ajuda, canto de alegria à sombra das tuas asas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14848, 'nvi', 'Salmos', 'sl', 63, 8, 'A minha alma apega-se a ti; a tua mão direita me sustém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14849, 'nvi', 'Salmos', 'sl', 63, 9, 'Aqueles, porém, que querem matar-me serão destruídos; descerão às profundezas da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14850, 'nvi', 'Salmos', 'sl', 63, 10, 'Serão entregues à espada e devorados por chacais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14851, 'nvi', 'Salmos', 'sl', 63, 11, 'Mas o rei se alegrará em Deus; todos os que juram pelo nome de Deus o louvarão, mas as bocas dos mentirosos serão tapadas.');
