INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14852, 'nvi', 'Salmos', 'sl', 64, 1, 'Ouve-me, ó Deus, quando faço a minha queixa; protege a minha vida do inimigo ameaçador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14853, 'nvi', 'Salmos', 'sl', 64, 2, 'Defende-me da conspiração dos ímpios e da ruidosa multidão de malfeitores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14854, 'nvi', 'Salmos', 'sl', 64, 3, 'Eles afiam a língua como espada e apontam como flechas, palavras envenenadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14855, 'nvi', 'Salmos', 'sl', 64, 4, 'De onde estão emboscados atiram no homem íntegro; atiram de surpresa, sem qualquer temor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14856, 'nvi', 'Salmos', 'sl', 64, 5, 'Animam-se uns aos outros com planos malignos, combinam como ocultar as suas armadilhas, e dizem: "Quem as verá? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14857, 'nvi', 'Salmos', 'sl', 64, 6, 'Tramam a injustiça e dizem: "Fizemos um plano perfeito! " A mente e o coração de cada um deles o encobrem!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14858, 'nvi', 'Salmos', 'sl', 64, 7, 'Mas Deus atirará neles suas flechas; repentinamente serão atingidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14859, 'nvi', 'Salmos', 'sl', 64, 8, 'Pelas próprias palavras farão cair uns aos outros; menearão a cabeça e zombarão deles todos os que os virem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14860, 'nvi', 'Salmos', 'sl', 64, 9, 'Todos os homens temerão, proclamarão as obras de Deus, refletindo no que ele fez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14861, 'nvi', 'Salmos', 'sl', 64, 10, 'Alegrem-se os justos no Senhor e nele busquem refúgio; congratulem-se todos os retos de coração!');
