INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14895, 'nvi', 'Salmos', 'sl', 67, 1, 'Que Deus tenha misericórdia de nós e nos abençoe, e faça resplandecer o seu rosto sobre nós, Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14896, 'nvi', 'Salmos', 'sl', 67, 2, 'para que sejam conhecidos na terra os teus caminhos, a tua salvação entre todas as nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14897, 'nvi', 'Salmos', 'sl', 67, 3, 'Louvem-te os povos, ó Deus; louvem-te todos os povos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14898, 'nvi', 'Salmos', 'sl', 67, 4, 'Exultem e cantem de alegria as nações, pois governas os povos com justiça e guias as nações na terra. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14899, 'nvi', 'Salmos', 'sl', 67, 5, 'Louvem-te os povos, ó Deus; louvem-te todos os povos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14900, 'nvi', 'Salmos', 'sl', 67, 6, 'Que a terra dê a sua colheita, e Deus, o nosso Deus, nos abençoe!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14901, 'nvi', 'Salmos', 'sl', 67, 7, 'Que Deus nos abençoe, e o temam todos os confins da terra.');
