INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14902, 'nvi', 'Salmos', 'sl', 68, 1, 'Que Deus se levante! Sejam espalhados os seus inimigos, fujam dele os seus adversários.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14903, 'nvi', 'Salmos', 'sl', 68, 2, 'Que tu os dissipes assim como o vento leva a fumaça; Como a cera se derrete na presença do fogo, assim pereçam os ímpios na presença de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14904, 'nvi', 'Salmos', 'sl', 68, 3, 'Alegrem-se, porém, os justos! Exultem diante de Deus! Regozijem-se com grande alegria!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14905, 'nvi', 'Salmos', 'sl', 68, 4, 'Cantem a Deus, louvem o seu nome, exaltem aquele que cavalga sobre as nuvens; seu nome é Senhor! Exultem diante dele!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14906, 'nvi', 'Salmos', 'sl', 68, 5, 'Pai para os órfãos e defensor das viúvas é Deus em sua santa habitação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14907, 'nvi', 'Salmos', 'sl', 68, 6, 'Deus dá um lar aos solitários, liberta os presos para a prosperidade, mas os rebeldes vivem em terra árida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14908, 'nvi', 'Salmos', 'sl', 68, 7, 'Quando saíste à frente do teu povo, ó Deus, quando marchaste pelo ermo, Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14909, 'nvi', 'Salmos', 'sl', 68, 8, 'a terra tremeu, o céu derramou chuva diante de Deus, o Deus do Sinai, diante de Deus, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14910, 'nvi', 'Salmos', 'sl', 68, 9, 'Deste chuvas generosas, ó Deus; refrescaste a tua herança exausta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14911, 'nvi', 'Salmos', 'sl', 68, 10, 'O teu povo nela se instalou, e da tua bondade, ó Deus, supriste os pobres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14912, 'nvi', 'Salmos', 'sl', 68, 11, 'O Senhor anunciou a palavra, e muitos mensageiros a proclamavam:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14913, 'nvi', 'Salmos', 'sl', 68, 12, '"Reis e exércitos fogem em debandada; a dona de casa reparte os despojos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14914, 'nvi', 'Salmos', 'sl', 68, 13, 'Mesmo quando vocês dormem entre as fogueiras do acampamento, as asas da minha pomba estão recobertas de prata, as suas penas, de ouro reluzente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14915, 'nvi', 'Salmos', 'sl', 68, 14, 'Quando o Todo-poderoso espalhou os reis, foi como neve no monte Zalmom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14916, 'nvi', 'Salmos', 'sl', 68, 15, 'Os montes de Basã são majestosos; escarpados são os montes de Basã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14917, 'nvi', 'Salmos', 'sl', 68, 16, 'Por que, ó montes escarpados, estão com inveja do monte que Deus escolheu para sua habitação, onde o próprio Senhor habitará para sempre?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14918, 'nvi', 'Salmos', 'sl', 68, 17, 'Os carros de Deus são incontáveis, milhares de milhares; neles o Senhor veio do Sinai para o seu Lugar Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14919, 'nvi', 'Salmos', 'sl', 68, 18, 'Quando subiste em triunfo às alturas, levaste cativo muitos prisioneiros; recebeste homens como dádivas, até mesmo rebeldes, para estabeleceres morada, ó Senhor Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14920, 'nvi', 'Salmos', 'sl', 68, 19, 'Bendito seja o Senhor, Deus, nosso Salvador, que cada dia suporta as nossas cargas. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14921, 'nvi', 'Salmos', 'sl', 68, 20, 'O nosso Deus é um Deus que salva; ele é o Soberano Senhor que nos livra da morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14922, 'nvi', 'Salmos', 'sl', 68, 21, 'Certamente Deus esmagará a cabeça dos seus inimigos, o crânio cabeludo dos que persistem em seus pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14923, 'nvi', 'Salmos', 'sl', 68, 22, '"Eu os trarei de Basã", diz o Senhor, "eu os trarei das profundezas do mar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14924, 'nvi', 'Salmos', 'sl', 68, 23, 'para que você encharque os pés no sangue dos inimigos, sangue do qual a língua dos cães terá a sua porção. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14925, 'nvi', 'Salmos', 'sl', 68, 24, 'Já se vê a tua marcha triunfal, ó Deus, a marcha do meu Deus e Rei adentrando o santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14926, 'nvi', 'Salmos', 'sl', 68, 25, 'À frente estão os cantores, depois os músicos; com eles vão as jovens tocando tamborins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14927, 'nvi', 'Salmos', 'sl', 68, 26, 'Bendigam a Deus na grande congregação! Bendigam o Senhor, descendentes de Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14928, 'nvi', 'Salmos', 'sl', 68, 27, 'Ali está a pequena tribo de Benjamim, a conduzi-los, os príncipes de Judá acompanhados de suas tropas, e os príncipes de Zebulom e Naftali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14929, 'nvi', 'Salmos', 'sl', 68, 28, 'A favor de vocês, manifeste Deus o seu poder! Mostra, ó Deus, o poder que já tens operado para conosco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14930, 'nvi', 'Salmos', 'sl', 68, 29, 'Por causa do teu templo em Jerusalém, reis te trarão presentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14931, 'nvi', 'Salmos', 'sl', 68, 30, 'Repreende a fera entre os juncos, a manada de touros entre os bezerros das nações. Humilhados, tragam barras de prata. Espalha as nações que têm prazer na guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14932, 'nvi', 'Salmos', 'sl', 68, 31, 'Ricos tecidos venham do Egito; a Etiópia corra para Deus de mãos cheias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14933, 'nvi', 'Salmos', 'sl', 68, 32, 'Cantem a Deus, reinos da terra, louvem o Senhor, Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14934, 'nvi', 'Salmos', 'sl', 68, 33, 'àquele que cavalga os céus, os antigos céus. Escutem! Ele troveja com voz poderosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14935, 'nvi', 'Salmos', 'sl', 68, 34, 'Proclamem o poder de Deus! Sua majestade está sobre Israel, seu poder está nas altas nuvens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14936, 'nvi', 'Salmos', 'sl', 68, 35, 'Tu és temível no teu santuário, ó Deus; é o Deus de Israel que dá poder e força ao seu povo. Bendito seja Deus!');
