INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14937, 'nvi', 'Salmos', 'sl', 69, 1, 'Salva-me, ó Deus!, pois as águas subiram até o meu pescoço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14938, 'nvi', 'Salmos', 'sl', 69, 2, 'Nas profundezas lamacentas eu me afundo, não tenho onde firmar os pés. Entrei em águas profundas; as correntezas me arrastam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14939, 'nvi', 'Salmos', 'sl', 69, 3, 'Cansei-me de pedir socorro; minha garganta se abrasa. Meus olhos fraquejam de tanto esperar pelo meu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14940, 'nvi', 'Salmos', 'sl', 69, 4, 'Os que sem razão me odeiam são mais do que os fios de cabelo da minha cabeça; muitos são os que me prejudicam sem motivo, muitos, os que procuram destruir-me. Sou forçado a devolver o que não roubei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14941, 'nvi', 'Salmos', 'sl', 69, 5, 'Tu bem sabes como fui insensato, ó Deus; a minha culpa não te é encoberta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14942, 'nvi', 'Salmos', 'sl', 69, 6, 'Não se decepcionem por minha causa aqueles que esperam em ti, ó Senhor, Senhor dos Exércitos! Não se frustrem por minha causa os que te buscam, ó Deus de Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14943, 'nvi', 'Salmos', 'sl', 69, 7, 'Pois por amor a ti suporto zombaria, e a vergonha cobre-me o rosto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14944, 'nvi', 'Salmos', 'sl', 69, 8, 'Sou um estrangeiro para os meus irmãos, um estranho até para os filhos da minha mãe;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14945, 'nvi', 'Salmos', 'sl', 69, 9, 'pois o zelo pela tua casa me consome, e os insultos daqueles que te insultam caem sobre mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14946, 'nvi', 'Salmos', 'sl', 69, 10, 'Até quando choro e jejuo, tenho que suportar zombaria;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14947, 'nvi', 'Salmos', 'sl', 69, 11, 'quando ponho vestes de lamento, sou motivo de piada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14948, 'nvi', 'Salmos', 'sl', 69, 12, 'Os que se ajuntam na praça falam de mim, e sou a canção dos bêbados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14949, 'nvi', 'Salmos', 'sl', 69, 13, 'Mas eu, Senhor, no tempo oportuno, elevo a ti minha oração; responde-me, por teu grande amor, ó Deus, com a tua salvação infalível!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14950, 'nvi', 'Salmos', 'sl', 69, 14, 'Tira-me do atoleiro, não me deixes afundar; liberta-me dos que me odeiam e das águas profundas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14951, 'nvi', 'Salmos', 'sl', 69, 15, 'Não permitas que as correntezas me arrastem, nem que as profundezas me engulam, nem que a cova feche sobre mim a sua boca!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14952, 'nvi', 'Salmos', 'sl', 69, 16, 'Responde-me, Senhor, pela bondade do teu amor; por tua grande misericórdia, volta-te para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14953, 'nvi', 'Salmos', 'sl', 69, 17, 'Não escondas do teu servo a tua face; responde-me depressa, pois estou em perigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14954, 'nvi', 'Salmos', 'sl', 69, 18, 'Aproxima-te e resgata-me; livra-me por causa dos meus inimigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14955, 'nvi', 'Salmos', 'sl', 69, 19, 'Tu bem sabes como sofro zombaria, humilhação e vergonha; conheces todos os meus adversários.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14956, 'nvi', 'Salmos', 'sl', 69, 20, 'A zombaria partiu-me o coração; estou em desespero! Supliquei por socorro, nada recebi, por consoladores, e a ninguém encontrei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14957, 'nvi', 'Salmos', 'sl', 69, 21, 'Puseram fel na minha comida e para matar-me a sede deram-me vinagre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14958, 'nvi', 'Salmos', 'sl', 69, 22, 'Que a mesa deles se lhes transforme em laço; torne-se retribuição e armadilha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14959, 'nvi', 'Salmos', 'sl', 69, 23, 'Escureçam-se os seus olhos para que não consigam ver; faze-lhes tremer o corpo sem parar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14960, 'nvi', 'Salmos', 'sl', 69, 24, 'Despeja sobre eles a tua ira; que o teu furor ardente os alcance.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14961, 'nvi', 'Salmos', 'sl', 69, 25, 'Fique deserto o lugar deles; não haja ninguém que habite nas suas tendas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14962, 'nvi', 'Salmos', 'sl', 69, 26, 'Pois perseguem aqueles que tu feres e comentam a dor daqueles a quem castigas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14963, 'nvi', 'Salmos', 'sl', 69, 27, 'Acrescenta-lhes pecado sobre pecado; não os deixes alcançar a tua justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14964, 'nvi', 'Salmos', 'sl', 69, 28, 'Sejam eles tirados do livro da vida e não sejam incluídos no rol dos justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14965, 'nvi', 'Salmos', 'sl', 69, 29, 'Grande é a minha aflição e a minha dor! Proteja-me, ó Deus, a tua salvação!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14966, 'nvi', 'Salmos', 'sl', 69, 30, 'Louvarei o nome de Deus com cânticos e proclamarei sua grandeza com ações de graças;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14967, 'nvi', 'Salmos', 'sl', 69, 31, 'isso agradará o Senhor mais do que bois, mais do que touros com seus chifres e cascos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14968, 'nvi', 'Salmos', 'sl', 69, 32, 'Os necessitados o verão e se alegrarão; a vocês que buscam a Deus, vida ao seu coração!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14969, 'nvi', 'Salmos', 'sl', 69, 33, 'O Senhor ouve o pobre e não despreza o seu povo aprisionado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14970, 'nvi', 'Salmos', 'sl', 69, 34, 'Louvem-no os céus e a terra, os mares e tudo o que neles se move,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14971, 'nvi', 'Salmos', 'sl', 69, 35, 'pois Deus salvará Sião e reconstruirá as cidades de Judá. Então o povo ali viverá e tomará posse da terra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14972, 'nvi', 'Salmos', 'sl', 69, 36, 'a descendência dos seus servos a herdará, e nela habitarão os que amam o seu nome.');
