INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13997, 'nvi', 'Salmos', 'sl', 7, 1, 'Senhor meu Deus, em ti me refugio; salva-me e livra-me de todos os que me perseguem,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13998, 'nvi', 'Salmos', 'sl', 7, 2, 'para que, como leões, não me dilacerem nem me despedacem, sem que ninguém me livre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13999, 'nvi', 'Salmos', 'sl', 7, 3, 'Senhor meu Deus, se assim procedi, se nas minhas mãos há injustiça,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14000, 'nvi', 'Salmos', 'sl', 7, 4, 'se fiz algum mal a um amigo ou se poupei sem motivo o meu adversário,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14001, 'nvi', 'Salmos', 'sl', 7, 5, 'persiga-me o meu inimigo até me alcançar, no chão me pisoteie e aniquile a minha vida, lançando a minha honra no pó. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14002, 'nvi', 'Salmos', 'sl', 7, 6, 'Levanta-te, Senhor, na tua ira; ergue-te contra o furor dos meus adversários. Desperta-te, meu Deus! Ordena a justiça!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14003, 'nvi', 'Salmos', 'sl', 7, 7, 'Reúnam-se os povos ao teu redor. Das alturas reina sobre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14004, 'nvi', 'Salmos', 'sl', 7, 8, 'O Senhor é quem julga os povos. Julga-me, Senhor, conforme a minha justiça, conforme a minha integridade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14005, 'nvi', 'Salmos', 'sl', 7, 9, 'Deus justo, que sondas as mentes e os corações, dá fim à maldade dos ímpios e ao justo dá segurança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14006, 'nvi', 'Salmos', 'sl', 7, 10, 'O meu escudo está nas mãos de Deus, que salva o reto de coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14007, 'nvi', 'Salmos', 'sl', 7, 11, 'Deus é um juiz justo, um Deus que manifesta cada dia o seu furor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14008, 'nvi', 'Salmos', 'sl', 7, 12, 'Se o homem não se arrepende, Deus afia a sua espada, arma o seu arco e o aponta,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14009, 'nvi', 'Salmos', 'sl', 7, 13, 'prepara as suas armas mortais e faz de suas setas flechas flamejantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14010, 'nvi', 'Salmos', 'sl', 7, 14, 'Quem gera a maldade, concebe sofrimento e dá à luz a desilusão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14011, 'nvi', 'Salmos', 'sl', 7, 15, 'Quem cava um buraco e o aprofunda cairá nessa armadilha que fez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14012, 'nvi', 'Salmos', 'sl', 7, 16, 'Sua maldade se voltará contra ele; sua violência cairá sobre a sua própria cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14013, 'nvi', 'Salmos', 'sl', 7, 17, 'Darei graças ao Senhor por sua justiça; Ao nome do Senhor Altíssimo cantarei louvores.');
