INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14973, 'nvi', 'Salmos', 'sl', 70, 1, 'Livra-me, ó Deus! Apressa-te, Senhor, a ajudar-me!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14974, 'nvi', 'Salmos', 'sl', 70, 2, 'Sejam humilhados e frustrados os que procuram tirar-me a vida; retrocedam desprezados os que desejam a minha ruína.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14975, 'nvi', 'Salmos', 'sl', 70, 3, 'Retrocedam em desgraça os que zombam de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14976, 'nvi', 'Salmos', 'sl', 70, 4, 'Mas regozijem-se e alegrem-se em ti todos os que te buscam; digam sempre os que amam a tua salvação: "Como Deus é grande! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14977, 'nvi', 'Salmos', 'sl', 70, 5, 'Quanto a mim, sou pobre e necessitado; apressa-te, ó Deus. Tu és o meu socorro e o meu libertador; Senhor, não te demores!');
