INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14978, 'nvi', 'Salmos', 'sl', 71, 1, 'Em ti, Senhor, busquei refúgio; nunca permitas que eu seja humilhado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14979, 'nvi', 'Salmos', 'sl', 71, 2, 'Resgata-me e livra-me por tua justiça; inclina o teu ouvido para mim e salva-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14980, 'nvi', 'Salmos', 'sl', 71, 3, 'Peço-te que sejas a minha rocha de refúgio, para onde eu sempre possa ir; dá ordem para que me libertem, pois és a minha rocha e a minha fortaleza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14981, 'nvi', 'Salmos', 'sl', 71, 4, 'Livra-me, ó meu Deus, das mãos dos ímpios, das garras dos perversos e cruéis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14982, 'nvi', 'Salmos', 'sl', 71, 5, 'Pois tu és a minha esperança, ó Soberano Senhor, em ti está a minha confiança desde a juventude.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14983, 'nvi', 'Salmos', 'sl', 71, 6, 'Desde o ventre materno dependo de ti; tu me sustentaste desde as entranhas de minha mãe. Eu sempre te louvarei!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14984, 'nvi', 'Salmos', 'sl', 71, 7, 'Tornei-me um exemplo para muitos, porque tu és o meu refúgio seguro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14985, 'nvi', 'Salmos', 'sl', 71, 8, 'Do teu louvor transborda a minha boca, que o tempo todo proclama o teu esplendor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14986, 'nvi', 'Salmos', 'sl', 71, 9, 'Não me rejeites na minha velhice; não me abandones quando se vão as minhas forças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14987, 'nvi', 'Salmos', 'sl', 71, 10, 'Pois os meus inimigos me caluniam; os que estão à espreita juntam-se e planejam matar-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14988, 'nvi', 'Salmos', 'sl', 71, 11, '"Deus o abandonou", dizem eles; "persigam-no e prendam-no, pois ninguém o livrará". -');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14989, 'nvi', 'Salmos', 'sl', 71, 12, 'Não fiques longe de mim, ó Deus; ó meu Deus, apressa-te em ajudar-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14990, 'nvi', 'Salmos', 'sl', 71, 13, 'Pereçam humilhados os meus acusadores; sejam cobertos de zombaria e vergonha os que querem prejudicar-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14991, 'nvi', 'Salmos', 'sl', 71, 14, 'Mas eu sempre terei esperança e te louvarei cada vez mais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14992, 'nvi', 'Salmos', 'sl', 71, 15, 'A minha boca falará sem cessar da tua justiça e dos teus incontáveis atos de salvação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14993, 'nvi', 'Salmos', 'sl', 71, 16, 'Falarei dos teus feitos poderosos, ó Soberano Senhor; proclamarei a tua justiça, unicamente a tua justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14994, 'nvi', 'Salmos', 'sl', 71, 17, 'Desde a minha juventude, ó Deus, tens me ensinado, e até hoje eu anuncio as tuas maravilhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14995, 'nvi', 'Salmos', 'sl', 71, 18, 'Agora que estou velho, de cabelos brancos, não me abandones, ó Deus, para que eu possa falar da tua força aos nossos filhos, e do teu poder às futuras gerações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14996, 'nvi', 'Salmos', 'sl', 71, 19, 'Tua justiça chega até as alturas, ó Deus, tu, que tens feito coisas grandiosas. Quem se compara a ti, ó Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14997, 'nvi', 'Salmos', 'sl', 71, 20, 'Tu, que me fizeste passar muitas e duras tribulações, restaurarás a minha vida, e das profundezas da terra de novo me farás subir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14998, 'nvi', 'Salmos', 'sl', 71, 21, 'Tu me farás mais honrado e mais uma vez me consolarás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14999, 'nvi', 'Salmos', 'sl', 71, 22, 'E eu te louvarei com a lira por tua fidelidade, ó meu Deus; cantarei louvores a ti com a harpa, ó Santo de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15000, 'nvi', 'Salmos', 'sl', 71, 23, 'Os meus lábios gritarão de alegria quando eu cantar louvores a ti, pois tu me redimiste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15001, 'nvi', 'Salmos', 'sl', 71, 24, 'Também a minha língua sempre falará dos teus atos de justiça, pois os que queriam prejudicar-me foram humilhados e ficaram frustrados.');
