INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15073, 'nvi', 'Salmos', 'sl', 75, 1, 'Damos-te graças, ó Deus, damos graças, pois perto está o teu nome; todos falam dos teus feitos maravilhosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15074, 'nvi', 'Salmos', 'sl', 75, 2, 'Tu dizes: "Eu determino o tempo em que julgarei com justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15075, 'nvi', 'Salmos', 'sl', 75, 3, 'Quando treme a terra com todos os seus habitantes, sou eu que mantenho firmes as suas colunas. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15076, 'nvi', 'Salmos', 'sl', 75, 4, 'Aos arrogantes digo: ‘Parem de vangloriar-se! ’ E aos ímpios: ‘Não se rebelem!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15077, 'nvi', 'Salmos', 'sl', 75, 5, 'Não se rebelem contra os céus; não falem com insolência’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15078, 'nvi', 'Salmos', 'sl', 75, 6, 'Não é do Oriente nem do Ocidente nem do deserto que vem a exaltação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15079, 'nvi', 'Salmos', 'sl', 75, 7, 'É Deus quem julga: Humilha a um, a outro exalta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15080, 'nvi', 'Salmos', 'sl', 75, 8, 'Na mão do Senhor está um cálice cheio de vinho espumante e misturado; ele o derrama, e todos os ímpios da terra o bebem até a última gota.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15081, 'nvi', 'Salmos', 'sl', 75, 9, 'Quanto a mim, para sempre anunciarei essas coisas; cantarei louvores ao Deus de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15082, 'nvi', 'Salmos', 'sl', 75, 10, 'Destruirei o poder de todos os ímpios, mas o poder dos justos aumentará.');
