INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14014, 'nvi', 'Salmos', 'sl', 8, 1, 'Senhor, Senhor nosso, como é majestoso o teu nome em toda a terra! Tu, cuja glória é cantada nos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14015, 'nvi', 'Salmos', 'sl', 8, 2, 'Dos lábios das crianças e dos recém-nascidos firmaste o teu nome como fortaleza, por causa dos teus adversários, para silenciar o inimigo que busca vingança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14016, 'nvi', 'Salmos', 'sl', 8, 3, 'Quando contemplo os teus céus, obra dos teus dedos, a lua e as estrelas que ali firmaste,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14017, 'nvi', 'Salmos', 'sl', 8, 4, 'pergunto: Que é o homem, para que com ele te importes? E o filho do homem, para que com ele te preocupes?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14018, 'nvi', 'Salmos', 'sl', 8, 5, 'Tu o fizeste um pouco menor do que os seres celestiais e o coroaste de glória e de honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14019, 'nvi', 'Salmos', 'sl', 8, 6, 'Tu o fizeste dominar sobre as obras das tuas mãos; sob os seus pés tudo puseste:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14020, 'nvi', 'Salmos', 'sl', 8, 7, 'Todos os rebanhos e manadas, e até os animais selvagens,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14021, 'nvi', 'Salmos', 'sl', 8, 8, 'as aves do céu, os peixes do mar e tudo o que percorre as veredas dos mares.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (14022, 'nvi', 'Salmos', 'sl', 8, 9, 'Senhor, Senhor nosso, como é majestoso o teu nome em toda a terra!');
