INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15219, 'nvi', 'Salmos', 'sl', 81, 1, 'Cantem de alegria a Deus, nossa força; aclamem o Deus de Jacó!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15220, 'nvi', 'Salmos', 'sl', 81, 2, 'Comecem o louvor, façam ressoar o tamborim, toquem a lira e a harpa melodiosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15221, 'nvi', 'Salmos', 'sl', 81, 3, 'Toquem a trombeta na lua nova e no dia de lua cheia, dia da nossa festa;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15222, 'nvi', 'Salmos', 'sl', 81, 4, 'porque este é um decreto para Israel, uma ordenança do Deus de Jacó,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15223, 'nvi', 'Salmos', 'sl', 81, 5, 'que ele estabeleceu como estatuto para José, quando atacou o Egito. Ali ouvimos uma língua que não conhecíamos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15224, 'nvi', 'Salmos', 'sl', 81, 6, 'Ele diz: "Tirei o peso dos seus ombros; suas mãos ficaram livres dos cestos de cargas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15225, 'nvi', 'Salmos', 'sl', 81, 7, 'Na sua aflição vocês clamaram e eu os livrei, do esconderijo dos trovões lhes respondi; eu os pus à prova nas águas de Meribá. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15226, 'nvi', 'Salmos', 'sl', 81, 8, '"Ouça, meu povo, as minhas advertências; se tão-somente você me escutasse, ó Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15227, 'nvi', 'Salmos', 'sl', 81, 9, 'Não tenha deus estrangeiro no seu meio; não se incline perante nenhum deus estranho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15228, 'nvi', 'Salmos', 'sl', 81, 10, 'Eu sou o Senhor, o seu Deus, que o tirei da terra do Egito. Abra a sua boca, e eu o alimentarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15229, 'nvi', 'Salmos', 'sl', 81, 11, '"Mas o meu povo não quis ouvir-me; Israel não quis obedecer-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15230, 'nvi', 'Salmos', 'sl', 81, 12, 'Por isso os entreguei ao seu coração obstinado, para seguirem os seus próprios planos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15231, 'nvi', 'Salmos', 'sl', 81, 13, '"Se o meu povo apenas me ouvisse, se Israel seguisse os meus caminhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15232, 'nvi', 'Salmos', 'sl', 81, 14, 'com rapidez eu subjugaria os seus inimigos e voltaria a minha mão contra os seus adversários!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15233, 'nvi', 'Salmos', 'sl', 81, 15, 'Os que odeiam o Senhor se renderiam diante dele, e receberiam um castigo perpétuo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15234, 'nvi', 'Salmos', 'sl', 81, 16, 'Mas eu sustentaria Israel com o melhor trigo, e com o mel da rocha eu o satisfaria".');
