INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15235, 'nvi', 'Salmos', 'sl', 82, 1, 'É Deus quem preside na assembléia divina; no meio dos deuses, ele é o juiz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15236, 'nvi', 'Salmos', 'sl', 82, 2, '"Até quando vocês vão absolver os culpados e favorecer os ímpios? Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15237, 'nvi', 'Salmos', 'sl', 82, 3, 'Garantam justiça para os fracos e para os órfãos; mantenham os direitos dos necessitados e dos oprimidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15238, 'nvi', 'Salmos', 'sl', 82, 4, 'Livrem os fracos e os pobres; libertem-nos das mãos dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15239, 'nvi', 'Salmos', 'sl', 82, 5, 'Nada sabem, nada entendem. Vagueiam pelas trevas; todos os fundamentos da terra estão abalados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15240, 'nvi', 'Salmos', 'sl', 82, 6, 'Eu disse: vocês são deuses, todos vocês são filhos do Altíssimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15241, 'nvi', 'Salmos', 'sl', 82, 7, 'Mas vocês morrerão como simples homens; cairão como qualquer outro governante. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15242, 'nvi', 'Salmos', 'sl', 82, 8, 'Levanta-te, ó Deus, julga a terra, pois todas as nações te pertencem');
