INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15243, 'nvi', 'Salmos', 'sl', 83, 1, 'Ó Deus, não te emudeças; não fiques em silêncio nem te detenhas, ó Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15244, 'nvi', 'Salmos', 'sl', 83, 2, 'Vê como se agitam os teus inimigos, como os teus adversários te desafiam de cabeça erguida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15245, 'nvi', 'Salmos', 'sl', 83, 3, 'Com astúcia conspiram contra o teu povo; tramam contra aqueles que são o teu tesouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15246, 'nvi', 'Salmos', 'sl', 83, 4, 'Eles dizem: "Venham, vamos destruí-los como nação, para que o nome de Israel não seja mais lembrado! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15247, 'nvi', 'Salmos', 'sl', 83, 5, 'Com um só propósito tramam juntos; é contra ti que fazem acordo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15248, 'nvi', 'Salmos', 'sl', 83, 6, 'as tendas de Edom e os ismaelitas, Moabe e os hagarenos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15249, 'nvi', 'Salmos', 'sl', 83, 7, 'Gebal, Amom e Amaleque, a Filístia, com os habitantes de Tiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15250, 'nvi', 'Salmos', 'sl', 83, 8, 'Até a Assíria a eles se aliou, e trouxe força aos descendentes de Ló. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15251, 'nvi', 'Salmos', 'sl', 83, 9, 'Trata-os como trataste Midiã, como trataste Sísera e Jabim no rio Quisom,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15252, 'nvi', 'Salmos', 'sl', 83, 10, 'os quais morreram em En-Dor e se tornaram esterco para a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15253, 'nvi', 'Salmos', 'sl', 83, 11, 'Faze com os seus nobres o que fizeste com Orebe e Zeebe, e com todos os seus príncipes o que fizeste com Zeba e Zalmuna,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15254, 'nvi', 'Salmos', 'sl', 83, 12, 'que disseram: "Vamos apossar-nos das pastagens de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15255, 'nvi', 'Salmos', 'sl', 83, 13, 'Faze-os como folhas secas levadas no redemoinho, ó meu Deus, como palha ao vento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15256, 'nvi', 'Salmos', 'sl', 83, 14, 'Assim como o fogo consome a floresta e as chamas incendeiam os montes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15257, 'nvi', 'Salmos', 'sl', 83, 15, 'persegue-os com o teu vendaval e aterroriza-os com a tua tempestade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15258, 'nvi', 'Salmos', 'sl', 83, 16, 'Cobre-lhes de vergonha o rosto até que busquem o teu nome, Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15259, 'nvi', 'Salmos', 'sl', 83, 17, 'Sejam eles humilhados e aterrorizados para sempre; pereçam em completa desgraça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15260, 'nvi', 'Salmos', 'sl', 83, 18, 'Saibam eles que tu, cujo nome é Senhor, somente tu, és o Altíssimo sobre toda a terra.');
