INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15261, 'nvi', 'Salmos', 'sl', 84, 1, 'Como é agradável o lugar da tua habitação, Senhor dos Exércitos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15262, 'nvi', 'Salmos', 'sl', 84, 2, 'A minha alma anela, e até desfalece pelos átrios do Senhor; o meu coração e o meu corpo cantam de alegria ao Deus vivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15263, 'nvi', 'Salmos', 'sl', 84, 3, 'Até o pardal achou um lar, e a andorinha um ninho para si, para abrigar os seus filhotes, um lugar perto do teu altar, ó Senhor dos Exércitos, meu Rei e meu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15264, 'nvi', 'Salmos', 'sl', 84, 4, 'Como são felizes os que habitam em tua casa; louvam-te sem cessar! Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15265, 'nvi', 'Salmos', 'sl', 84, 5, 'Como são felizes os que em ti encontram sua força, e os que são peregrinos de coração!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15266, 'nvi', 'Salmos', 'sl', 84, 6, 'Ao passarem pelo vale de Baca, fazem dele um lugar de fontes; as chuvas de outono também o enchem de cisternas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15267, 'nvi', 'Salmos', 'sl', 84, 7, 'Prosseguem o caminho de força em força, até que cada um se apresente a Deus em Sião.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15268, 'nvi', 'Salmos', 'sl', 84, 8, 'Ouve a minha oração, ó Senhor Deus dos Exércitos; escuta-me, ó Deus de Jacó. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15269, 'nvi', 'Salmos', 'sl', 84, 9, 'Olha, ó Deus, que és nosso escudo; trata com bondade o teu ungido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15270, 'nvi', 'Salmos', 'sl', 84, 10, 'Melhor é um dia nos teus átrios do que mil noutro lugar; prefiro ficar à porta da casa do meu Deus a habitar nas tendas dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15271, 'nvi', 'Salmos', 'sl', 84, 11, 'O Senhor Deus é sol e escudo; o Senhor concede favor e honra; não recusa nenhum bem aos que vivem com integridade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15272, 'nvi', 'Salmos', 'sl', 84, 12, 'Ó Senhor dos Exércitos, como é feliz aquele que em ti confia!');
