INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15273, 'nvi', 'Salmos', 'sl', 85, 1, 'Foste favorável à tua terra, ó Senhor; trouxeste restauração a Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15274, 'nvi', 'Salmos', 'sl', 85, 2, 'Perdoaste a culpa do teu povo e cobriste todos os seus pecados. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15275, 'nvi', 'Salmos', 'sl', 85, 3, 'Retiraste todo o teu furor e te afastaste da tua ira tremenda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15276, 'nvi', 'Salmos', 'sl', 85, 4, 'Restaura-nos mais uma vez, ó Deus, nosso Salvador, e desfaze o teu furor para conosco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15277, 'nvi', 'Salmos', 'sl', 85, 5, 'Ficarás indignado conosco para sempre? Prolongarás a tua ira por todas as gerações?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15278, 'nvi', 'Salmos', 'sl', 85, 6, 'Acaso não nos renovarás a vida, a fim de que o teu povo se alegre em ti?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15279, 'nvi', 'Salmos', 'sl', 85, 7, 'Mostra-nos o teu amor, ó Senhor, e concede-nos a tua salvação!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15280, 'nvi', 'Salmos', 'sl', 85, 8, 'Eu ouvirei o que Deus, o Senhor, disse: Ele promete paz ao seu povo, aos seus fiéis! Não voltem eles à insensatez!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15281, 'nvi', 'Salmos', 'sl', 85, 9, 'Perto está a salvação que ele trará aos que o temem, e a sua glória habitará em nossa terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15282, 'nvi', 'Salmos', 'sl', 85, 10, 'O amor e a fidelidade se encontrarão; a justiça e a paz se beijarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15283, 'nvi', 'Salmos', 'sl', 85, 11, 'A fidelidade brotará da terra, e a justiça descerá dos céus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15284, 'nvi', 'Salmos', 'sl', 85, 12, 'O Senhor nos trará bênçãos, e a nossa terra dará a sua colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15285, 'nvi', 'Salmos', 'sl', 85, 13, 'A justiça irá adiante dele e preparará o caminho para os seus passos.');
