INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15286, 'nvi', 'Salmos', 'sl', 86, 1, 'Inclina os teus ouvidos, ó Senhor, e responde-me, pois sou pobre e necessitado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15287, 'nvi', 'Salmos', 'sl', 86, 2, 'Guarda a minha vida, pois sou fiel a ti. Tu és o meu Deus; salva o teu servo que em ti confia!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15288, 'nvi', 'Salmos', 'sl', 86, 3, 'Misericórdia, Senhor, pois clamo a ti sem cessar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15289, 'nvi', 'Salmos', 'sl', 86, 4, 'Alegra o coração do teu servo, pois a ti, Senhor, elevo a minha alma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15290, 'nvi', 'Salmos', 'sl', 86, 5, 'Tu és bondoso e perdoador, Senhor, rico em graça para com todos os que te invocam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15291, 'nvi', 'Salmos', 'sl', 86, 6, 'Escuta a minha oração, Senhor; atenta para a minha súplica!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15292, 'nvi', 'Salmos', 'sl', 86, 7, 'No dia da minha angústia clamarei a ti, pois tu me responderás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15293, 'nvi', 'Salmos', 'sl', 86, 8, 'Nenhum dos deuses é comparável a ti, Senhor, nenhum deles pode fazer o que tu fazes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15294, 'nvi', 'Salmos', 'sl', 86, 9, 'Todas as nações que tu formaste virão e te adorarão, Senhor, glorificarão o teu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15295, 'nvi', 'Salmos', 'sl', 86, 10, 'Pois tu és grande e realizas feitos maravilhosos; só tu és Deus!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15296, 'nvi', 'Salmos', 'sl', 86, 11, 'Ensina-me o teu caminho, Senhor, para que eu ande na tua verdade; dá-me um coração inteiramente fiel, para que eu tema o teu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15297, 'nvi', 'Salmos', 'sl', 86, 12, 'De todo o meu coração te louvarei, Senhor, meu Deus; glorificarei o teu nome para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15298, 'nvi', 'Salmos', 'sl', 86, 13, 'Pois grande é o teu amor para comigo; tu me livraste das profundezas do Sheol.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15299, 'nvi', 'Salmos', 'sl', 86, 14, 'Os arrogantes estão me atacando, ó Deus; um bando de homens cruéis, gente que não faz caso de ti procura tirar-me a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15300, 'nvi', 'Salmos', 'sl', 86, 15, 'Mas tu, Senhor, és Deus compassivo e misericordioso, muito paciente, rico em amor e em fidelidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15301, 'nvi', 'Salmos', 'sl', 86, 16, 'Volta-te para mim! Tem misericórdia de mim! Concede a tua força a teu servo e salva o filho da tua serva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15302, 'nvi', 'Salmos', 'sl', 86, 17, 'Dá-me um sinal da tua bondade, para que os meus inimigos vejam e sejam humilhados, pois tu, Senhor, me ajudaste e me consolaste.');
