INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15303, 'nvi', 'Salmos', 'sl', 87, 1, 'Ele edificou sua cidade sobre o monte santo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15304, 'nvi', 'Salmos', 'sl', 87, 2, 'o Senhor ama as portas de Sião mais do que qualquer outro lugar de Jacó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15305, 'nvi', 'Salmos', 'sl', 87, 3, 'Coisas gloriosas são ditas de ti, ó cidade de Deus! Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15306, 'nvi', 'Salmos', 'sl', 87, 4, '"Entre os que me reconhecem incluirei Raabe e Babilônia, além da Filístia, de Tiro, e também da Etiópia, como se tivessem nascido em Sião. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15307, 'nvi', 'Salmos', 'sl', 87, 5, 'De fato, acerca de Sião se dirá: "Todos estes nasceram nela, e o próprio Altíssimo a estabelecerá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15308, 'nvi', 'Salmos', 'sl', 87, 6, 'O Senhor escreverá no registro dos povos: "Este nasceu ali". Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15309, 'nvi', 'Salmos', 'sl', 87, 7, 'Com danças e cânticos, dirão: "Em Sião estão as nossas origens! "');
