INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15310, 'nvi', 'Salmos', 'sl', 88, 1, 'Ó Senhor, Deus que me salva, a ti clamo dia e noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15311, 'nvi', 'Salmos', 'sl', 88, 2, 'Que a minha oração chegue diante de ti; inclina os teus ouvidos ao meu clamor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15312, 'nvi', 'Salmos', 'sl', 88, 3, 'Tenho sofrido tanto que a minha vida está à beira da sepultura!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15313, 'nvi', 'Salmos', 'sl', 88, 4, 'Sou contado entre os que descem à cova; sou como um homem que já não tem forças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15314, 'nvi', 'Salmos', 'sl', 88, 5, 'Fui colocado junto aos mortos, sou como os cadáveres que jazem no túmulo, dos quais já não te lembras, pois foram tirados de tua mão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15315, 'nvi', 'Salmos', 'sl', 88, 6, 'Puseste-me na cova mais profunda, na escuridão das profundezas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15316, 'nvi', 'Salmos', 'sl', 88, 7, 'Tua ira pesa sobre mim; com todas as tuas ondas me afligiste. Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15317, 'nvi', 'Salmos', 'sl', 88, 8, 'Afastaste de mim os meus melhores amigos e me tornaste repugnante para eles. Estou como um preso que não pode fugir;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15318, 'nvi', 'Salmos', 'sl', 88, 9, 'minhas vistas já estão fracas de tristeza. A ti, Senhor, clamo cada dia; a ti ergo as minhas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15319, 'nvi', 'Salmos', 'sl', 88, 10, 'Acaso mostras as tuas maravilhas aos mortos? Acaso os mortos se levantam e te louvam? Pausa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15320, 'nvi', 'Salmos', 'sl', 88, 11, 'Será que o teu amor é anunciado no túmulo, e a tua fidelidade, no Abismo da Morte?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15321, 'nvi', 'Salmos', 'sl', 88, 12, 'Acaso são conhecidas as tuas maravilhas na região das trevas, e os teus feitos de justiça, na terra do esquecimento?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15322, 'nvi', 'Salmos', 'sl', 88, 13, 'Mas eu, Senhor, a ti clamo por socorro; já de manhã a minha oração chega à tua presença.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15323, 'nvi', 'Salmos', 'sl', 88, 14, 'Por que, Senhor, me rejeitas e escondes de mim o teu rosto?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15324, 'nvi', 'Salmos', 'sl', 88, 15, 'Desde moço tenho sofrido e ando perto da morte; os teus terrores levaram-me ao desespero.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15325, 'nvi', 'Salmos', 'sl', 88, 16, 'Sobre mim se abateu a tua ira; os pavores que me causas me destruíram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15326, 'nvi', 'Salmos', 'sl', 88, 17, 'Cercam-me o dia todo como uma inundação; envolvem-me por completo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15327, 'nvi', 'Salmos', 'sl', 88, 18, 'Tiraste de mim os meus amigos e os meus companheiros; as trevas são a minha única companhia.');
