INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15380, 'nvi', 'Salmos', 'sl', 90, 1, 'Senhor, tu és o nosso refúgio, sempre, de geração em geração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15381, 'nvi', 'Salmos', 'sl', 90, 2, 'Antes de nascerem os montes e de criares a terra e o mundo, de eternidade a eternidade tu és Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15382, 'nvi', 'Salmos', 'sl', 90, 3, 'Fazes os homens voltarem ao pó, dizendo: "Retornem ao pó, seres humanos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15383, 'nvi', 'Salmos', 'sl', 90, 4, 'De fato, mil anos para ti são como o dia de ontem que passou, como as horas da noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15384, 'nvi', 'Salmos', 'sl', 90, 5, 'Como uma correnteza, tu arrastas os homens; são breves como o sono; são como a relva que brota ao amanhecer;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15385, 'nvi', 'Salmos', 'sl', 90, 6, 'germina e brota pela manhã, mas, à tarde, murcha e seca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15386, 'nvi', 'Salmos', 'sl', 90, 7, 'Somos consumidos pela tua ira e aterrorizados pelo teu furor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15387, 'nvi', 'Salmos', 'sl', 90, 8, 'Conheces as nossas iniqüidades; não escapam os nossos pecados secretos à luz da tua presença.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15388, 'nvi', 'Salmos', 'sl', 90, 9, 'Todos os nossos dias passam debaixo do teu furor; vão-se como um murmúrio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15389, 'nvi', 'Salmos', 'sl', 90, 10, 'Os anos de nossa vida chegam a setenta, ou a oitenta para os que têm mais vigor; entretanto, são anos difíceis e cheios de sofrimento, pois a vida passa depressa, e nós voamos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15390, 'nvi', 'Salmos', 'sl', 90, 11, 'Quem conhece o poder da tua ira? Pois o teu furor é tão grande como o temor que te é devido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15391, 'nvi', 'Salmos', 'sl', 90, 12, 'Ensina-nos a contar os nossos dias para que o nosso coração alcance sabedoria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15392, 'nvi', 'Salmos', 'sl', 90, 13, 'Volta-te, Senhor! Até quando será assim? Tem compaixão dos teus servos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15393, 'nvi', 'Salmos', 'sl', 90, 14, 'Satisfaze-nos pela manhã com o teu amor leal, e todos os nossos dias cantaremos felizes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15394, 'nvi', 'Salmos', 'sl', 90, 15, 'Dá-nos alegria pelo tempo que nos afligiste, pelos anos em que tanto sofremos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15395, 'nvi', 'Salmos', 'sl', 90, 16, 'Sejam manifestos os teus feitos aos teus servos, e aos filhos deles o teu esplendor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15396, 'nvi', 'Salmos', 'sl', 90, 17, 'Esteja sobre nós a bondade do nosso Deus Soberano. Consolida, para nós, a obra de nossas mãos; consolida a obra de nossas mãos!');
