INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15397, 'nvi', 'Salmos', 'sl', 91, 1, 'Aquele que habita no abrigo do Altíssimo e descansa à sombra do Todo-poderoso');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15398, 'nvi', 'Salmos', 'sl', 91, 2, 'pode dizer ao Senhor: Tu és o meu refúgio e a minha fortaleza, o meu Deus, em quem confio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15399, 'nvi', 'Salmos', 'sl', 91, 3, 'Ele o livrará do laço do caçador e do veneno mortal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15400, 'nvi', 'Salmos', 'sl', 91, 4, 'Ele o cobrirá com as suas penas, e sob as suas asas você encontrará refúgio; a fidelidade dele será o seu escudo protetor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15401, 'nvi', 'Salmos', 'sl', 91, 5, 'Você não temerá o pavor da noite, nem a flecha que voa de dia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15402, 'nvi', 'Salmos', 'sl', 91, 6, 'nem a peste que se move sorrateira nas trevas, nem a praga que devasta ao meio-dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15403, 'nvi', 'Salmos', 'sl', 91, 7, 'Mil poderão cair ao seu lado, dez mil à sua direita, mas nada o atingirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15404, 'nvi', 'Salmos', 'sl', 91, 8, 'Você simplesmente olhará, e verá o castigo dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15405, 'nvi', 'Salmos', 'sl', 91, 9, 'Se você fizer do Altíssimo o seu refúgio,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15406, 'nvi', 'Salmos', 'sl', 91, 10, 'nenhum mal o atingirá, desgraça alguma chegará à sua tenda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15407, 'nvi', 'Salmos', 'sl', 91, 11, 'Porque a seus anjos ele dará ordens a seu respeito, para que o protejam em todos os seus caminhos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15408, 'nvi', 'Salmos', 'sl', 91, 12, 'com as mãos eles o segurarão, para que você não tropece em alguma pedra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15409, 'nvi', 'Salmos', 'sl', 91, 13, 'Você pisará o leão e a cobra; pisoteará o leão forte e a serpente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15410, 'nvi', 'Salmos', 'sl', 91, 14, '"Porque ele me ama, eu o resgatarei; eu o protegerei, pois conhece o meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15411, 'nvi', 'Salmos', 'sl', 91, 15, 'Ele clamará a mim, e eu lhe darei resposta, e na adversidade estarei com ele; vou livrá-lo e cobri-lo de honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15412, 'nvi', 'Salmos', 'sl', 91, 16, 'Vida longa eu lhe darei, e lhe mostrarei a minha salvação. "');
