INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15413, 'nvi', 'Salmos', 'sl', 92, 1, 'Como é bom render graças ao Senhor e cantar louvores ao teu nome, ó Altíssimo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15414, 'nvi', 'Salmos', 'sl', 92, 2, 'anunciar de manhã o teu amor leal e de noite a tua fidelidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15415, 'nvi', 'Salmos', 'sl', 92, 3, 'ao som da lira de dez cordas e da cítara, e da melodia da harpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15416, 'nvi', 'Salmos', 'sl', 92, 4, 'Tu me alegras, Senhor, com os teus feitos; as obras das tuas mãos levam-me a cantar de alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15417, 'nvi', 'Salmos', 'sl', 92, 5, 'Como são grandes as tuas obras, Senhor, como são profundos os teus propósitos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15418, 'nvi', 'Salmos', 'sl', 92, 6, 'O insensato não entende, o tolo não vê');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15419, 'nvi', 'Salmos', 'sl', 92, 7, 'que, embora os ímpios brotem como a erva e floresçam todos os malfeitores, serão destruídos para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15420, 'nvi', 'Salmos', 'sl', 92, 8, 'Pois tu, Senhor, és exaltado para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15421, 'nvi', 'Salmos', 'sl', 92, 9, 'Mas os teus inimigos, Senhor, os teus inimigos perecerão; serão dispersos todos os malfeitores!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15422, 'nvi', 'Salmos', 'sl', 92, 10, 'Tu aumentaste a minha força como a do boi selvagem; derramaste sobre mim óleo novo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15423, 'nvi', 'Salmos', 'sl', 92, 11, 'Os meus olhos contemplaram a derrota dos meus inimigos; os meus ouvidos escutaram a debandada dos meus maldosos agressores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15424, 'nvi', 'Salmos', 'sl', 92, 12, 'Os justos florescerão como a palmeira, crescerão como o cedro do Líbano;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15425, 'nvi', 'Salmos', 'sl', 92, 13, 'plantados na casa do Senhor, florescerão nos átrios do nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15426, 'nvi', 'Salmos', 'sl', 92, 14, 'Mesmo na velhice darão fruto, permanecerão viçosos e verdejantes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15427, 'nvi', 'Salmos', 'sl', 92, 15, 'para proclamar que o Senhor é justo. Ele é a minha rocha; nele não há injustiça.');
