INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15428, 'nvi', 'Salmos', 'sl', 93, 1, 'O Senhor reina! Vestiu-se de majestade; de majestade vestiu-se o Senhor e armou-se de poder! O mundo está firme e não se abalará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15429, 'nvi', 'Salmos', 'sl', 93, 2, 'O teu trono está firme desde a antigüidade; tu existes desde a eternidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15430, 'nvi', 'Salmos', 'sl', 93, 3, 'As águas se levantaram, Senhor, as águas levantaram a voz; as águas levantaram seu bramido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15431, 'nvi', 'Salmos', 'sl', 93, 4, 'Mais poderoso do que o estrondo das águas impetuosas, mais poderoso do que as ondas do mar é o Senhor nas alturas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (15432, 'nvi', 'Salmos', 'sl', 93, 5, 'Os teus mandamentos permanecem firmes e fiéis; a santidade, Senhor, é o ornamento perpétuo da tua casa.');
